                        </Link>
                        <Date>{summary[1]}/{summary[2]}/{summary[3]}</Date>
                    </Summary>
                    <Extra text>
                        {summary[0]}
                    </Extra>
                    <Meta onClick={() => this.props.onClick()}>
                        <Like>
                            <Icon name='like' color={summary[10] ? 'red' : 'grey'} />
                            {summary[4]} likes
                        </Like>
                    </Meta>
                </Content>
            </Event>
        );
    }
}

export default PostRow;
