import React, { Component } from 'react';
import { Menu, Icon, Input, Segment } from 'semantic-ui-react';
import { Link, Router } from '../routes';

class Header extends Component {

  state = { activeItem: 'Home' };

  handleItemClick = (e, { name }) => {
    this.setState({ activeItem: name })
    if (name === 'Home'){
      Router.pushRoute('/');
    } else if (name === 'Profile') {
      Router.pushRoute('/userProfile');
    } else {
      Router.pushRoute('/new');
    }
  };

  render() {
    const { activeItem } = this.state;
    console.log(activeItem)
    return (
      <React.Fragment>
        <Segment style={{marginBottom:'0px', fontSize:'20px', borderRadius:'0px', backgroundColor:'#EDC7B7', border:'0px'}} as='h1' textAlign='center'>
          <Icon name='ethereum' />Ethereum-Based Question & Answer System
        </Segment>
        <Menu size='massive' style={{marginTop:'0px', borderRadius:'0px', backgroundColor:'#EEE2DC', border:'0px'}} color='orange'>
          <Link route="/">
            <Menu.Item
              name="Home"
              // active={activeItem === "Home"}
              // onClick={this.handleItemClick}
            />
          </Link>
          <Link route='/userProfile'>
            <Menu.Item
              name="Profile"
              // active={activeItem === "Profile"}
              // onClick={this.handleItemClick}
            />
          </Link>
            <Menu.Item
              name="Create New Question"
              active={activeItem === "Create New Question"}
              onClick={this.handleItemClick}
            />
          <Menu.Menu position="right">
            <Menu.Item>
              <Input icon="search" placeholder="Search..." />
            </Menu.Item>
          </Menu.Menu>
        </Menu>
      </React.Fragment>
    );
  }
}

export default Header;
