import React, { Component } from 'react';
import { Menu, Icon, Input, Segment } from 'semantic-ui-react';
import { Link } from '../routes';

class Header extends Component {
  state = { activeItem: "home" };

  handleItemClick = (e, { name }) => this.setState({ activeItem: name });

  render() {
    const { activeItem } = this.state;

    return (
      <React.Fragment>
        <Segment style={{marginBottom:'0px', fontSize:'20px', borderRadius:'0px', backgroundColor:'#EDC7B7', border:'0px'}} as='h1' textAlign='center' size='small'>
          <Icon name='ethereum' />Ethereum-Based Question & Answer System
        </Segment>
        <Menu size='massive' style={{marginTop:'0px', borderRadius:'0px', backgroundColor:'#EEE2DC', border:'0px'}}>
          <Link route='/'>
            <Menu.Item color='orange'
              name="home"
              active={activeItem === "home"}
              onClick={this.handleItemClick}
            />
          </Link>
          <Menu.Item
            name="messages"
            active={activeItem === "messages"}
            onClick={this.handleItemClick}
          />
          <Menu.Item
            name="friends"
            active={activeItem === "friends"}
            onClick={this.handleItemClick}
          />
          <Menu.Menu position="right">
            <Menu.Item>
              <Input icon="search" placeholder="Search..." />
            </Menu.Item>
          </Menu.Menu>
        </Menu>
      </React.Fragment>
    );
  }
