import React, { Component } from 'react';
import { Table, Popup, Button, Container, Form, Header, Comment } from 'semantic-ui-react';
import { Link } from '../routes';
import moment from 'moment';
import web3 from '../ethereum/web3';
import Question from '../ethereum/question';
import factory from '../ethereum/factory';
import Layout from '../components/Layout';
import Countdown from 'react-countdown-now';

class QuestionRow extends Component {
    state = {
      questionAddress: '',
      questionState: '',
      votingTime:'',
      answer:'',
      errorMessage: '',
    }

    static async getInitialProps(props) {
      const questionAddress = props.query.value;
      const question = Question(questionAddress);
      const summary = await question.methods.getSummary().call();
      const time = await question.methods.getTime().call();
      const profileOwner = await factory.methods.getProfile(summary[5]).call();
      const answerList = await question.methods.getAnswerList().call();
      return { summary, time, questionAddress, answerList };
    };

    static async getDerivedStateFromProps(props, state) {
      state.questionAddress = props.questionAddress;
      state.questionState = props.summary[4];
      state.votingTime = props.time[3];
    }

    handleClick = async () => {
      if(this.state.questionState == 0) {
        const accounts = await web3.eth.getAccounts();
        const question = Question(this.state.questionAddress);
        let _questionState = await question.methods.getQuestionState().send(
          { from: accounts[0]}
        );
        // Set voting time inside smart contract as +1hr of now end time
        let _votingTime = await question.methods.getTime().call();
        console.log(_votingTime);
        this.setState({questionState: _questionState, votingTime: _votingTime[3] });
      }
    };

    handleReward = () => {
      console.log("reward!")
    };

    onSubmitAnswer = async () => {
      event.preventDefault(); // prevent default submission of the form to the backend (the browser does that automatically)

      try {
          const accounts = await web3.eth.getAccounts();
          // Create a new post
          await factory.methods
          .createAnswer(this.state.questionAddress, this.state.answer)
          .send({
              from: accounts[0]
          });

          Router.pushRoute('/'); // Automatic redirect the user.
      } catch (err) {
          this.setState({ errorMessage: err.message });
      }

      this.setState({ loading: false });
    };

    renderQuestion() {
        const summary = this.props.summary;
        const time = this.props.time;
        var currentTime = (new Date).getTime();
        var maxDuration =(parseFloat(summary[3]) / 60 / 60).toFixed(2).toString().toString();
        var publishTime = moment.unix(time[0]).format('dddd, Do MMMM YYYY, h:mm:ss a');
        var votingTime = moment.unix(this.state.votingTime).format('dddd, Do MMMM YYYY, h:mm:ss a');
        var publishTimeMs = moment.unix(time[0]).valueOf();
        var maxDurationMs = moment.unix(time[2]).valueOf();
        var votingTimeMs = moment.unix(this.state.votingTime).valueOf();

        let remainingTime = 0;
        if (this.state.questionState == 0) {
          remainingTime = publishTimeMs + maxDurationMs - currentTime;
          if (remainingTime < 0) {
              remainingTime = 0;
          }
        } else {
          remainingTime = votingTimeMs + maxDurationMs - currentTime;
          if (remainingTime < 0) {
              remainingTime = 0;
          }
        }

        return (
          <React.Fragment>
              <Table definition>
                  <Table.Body>
                    <Table.Row>
                        <Table.Cell width={2}>Question Title</Table.Cell>
                        <Table.Cell style={{fontSize: '20px', lineHeight: '1.5'}}>
                            {summary[0]}
                        </Table.Cell>
                    </Table.Row>
                      <Table.Row>
                          <Table.Cell width={2}>Description</Table.Cell>
                          <Table.Cell style={{fontSize: '20px', lineHeight: '1.5'}}>
                              {summary[1]}
                          </Table.Cell>
                      </Table.Row>
                      <Table.Row>
                          <Table.Cell>Reward (ETH)</Table.Cell>
                          <Table.Cell style={{fontSize: '20px'}}>{summary[2]}</Table.Cell>
                      </Table.Row>
                      <Table.Row>
                          <Table.Cell>Publish Time</Table.Cell>
                          <Table.Cell style={{fontSize: '20px'}}>{publishTime}</Table.Cell>
                      </Table.Row>
                      <Table.Row>
                          <Table.Cell>Answering Duration (hours)</Table.Cell>
                          <Table.Cell style={{fontSize: '20px'}}>
                              <span style={{verticalAlign: 'middle', lineHeight: '33px'}}>
                                  {maxDuration}
                              </span>
                              {remainingTime > 0
                                  ? <Popup
                                      trigger={
                                          <Button style={{float: 'right', verticalAlign: 'middle'}}
                                                  icon='clock'
                                                  color="violet"
                                          />}
                                      content={
                                          <span>
                                          <span>Remaining time - </span>
                                          <Countdown date={Date.now() + remainingTime}>
                                          </Countdown>
                                      </span>}
                                      position='bottom right'
                                      inverted />
                                  : <Popup
                                      trigger={
                                          <Button style={{float: 'right', verticalAlign: 'middle'}}
                                                  icon='clock'
                                                  color="red"
                                                  onClick={this.handleClick}
                                          />
                                      }
                                      content={"EXPIRED"}
                                      position='bottom right'
                                      inverted />
                              }
                          </Table.Cell>
                      </Table.Row>
                      <Table.Row>
                          <Table.Cell>Voting Duration (hours)</Table.Cell>
                          <Table.Cell style={{fontSize: '20px'}}>
                              <span style={{verticalAlign: 'middle', lineHeight: '33px'}}>
                                  {maxDuration}
                              </span>
                              {this.state.questionState == 1 && remainingTime > 0
                                  ? <Popup
                                      trigger={
                                          <Button style={{float: 'right', verticalAlign: 'middle'}}
                                                  icon='clock'
                                                  color="violet"
                                          />}
                                      content={
                                          <span>
                                          <span>Remaining time - </span>
                                          <Countdown date={Date.now() + remainingTime}>
                                          </Countdown>
                                      </span>}
                                      position='bottom right'
                                      inverted />
                                  : this.state.questionState == 1
                                  ? <Popup
                                      trigger={
                                          <Button style={{float: 'right', verticalAlign: 'middle'}}
                                                  icon='clock'
                                                  color="red"
                                                  onClick={this.handleReward}
                                                    />
                                      }
                                      content={"Reward"}
                                      position='bottom right'
                                      inverted />
                                  : this.state.questionState == 0
                                  ? <Popup
                                      trigger={
                                          <Button style={{float: 'right', verticalAlign: 'middle'}}
                                                  icon='clock'
                                                  color="red"
                                                  disabled
                                          />
                                      }
                                      position='bottom right'
                                      inverted />
                                  : <Popup
                                      trigger={
                                          <Button style={{float: 'right', verticalAlign: 'middle'}}
                                                  icon='clock'
                                                  color="red"
                                                  onClick={this.handleReward}
                                                    />
                                      }
                                      content={"Reward"}
                                      position='bottom right'
                                      inverted />
                            }
                          </Table.Cell>
                      </Table.Row>
                  </Table.Body>
              </Table>
          </React.Fragment>
        );
    }

    renderAnswers() {
      const answers = this.props.answerList;

      return answers.map((answer, index) => {
          return
            <Comment key={index}>
              <Comment.Avatar src='https://react.semantic-ui.com/images/avatar/small/matt.jpg' />
              <Comment.Content>
                <Comment.Author as='a'>Matt</Comment.Author>
                <Comment.Metadata>
                  <div>Today at 5:42PM</div>
                </Comment.Metadata>
                <Comment.Text>This is a very long comment. This is a very long comment. This is a very long comment. This is a very long comment. </Comment.Text>
                <Comment.Actions>
                  <Comment.Action>
                    <Button style={{float: 'right', verticalAlign: 'middle'}}
                            icon='thumbs down'
                            onClick={this.handleReward}
                            size='tiny'
                    />
                    <Button style={{float: 'right', verticalAlign: 'middle'}}
                            icon='thumbs up'
                            onClick={this.handleReward}
                            size='tiny'
                    />
                      </Comment.Action>
                </Comment.Actions>
              </Comment.Content>
            </Comment>
      });
    }

    renderForm() {
      return(
        <Form onSubmit={this.onSubmitAnswer}>
          <Form.TextArea
            value={this.state.answer}
            onChange={event => this.setState({ answer: event.target.value })}
          />
          <Button content='Submit Answers' labelPosition='left' icon='edit' primary />
        </Form>
      );
    }

    render() {
      return(
        <Layout>
          <Container>
            {this.renderQuestion()}
            <Header as='h2' textAlign='center'>
                Submit Your Answers Here
            </Header>
            {this.renderForm()}
            <Comment.Group size='large'>
            {this.renderAnswers()}
            </Comment.Group>
          </Container>
        </Layout>
      )
    }

}

export default QuestionRow;
