import React, { Component } from 'react';
import Layout from '../components/Layout';
import { Container, Segment, Header, Divider, Button, Icon, Radio } from "semantic-ui-react";

class FeedbackPage extends Component {

  state = {
    like: "",
    likeGreen:"false",
    likeRed:"false",
    usefulness: "",
    easy: ""
  }

  render() {
    return(
      <Layout>
        <Container style={{height:"100vh"}}>
          <Divider hidden/>
          <Segment raised textAlign='left'>
            <Header as='h2' textAlign='center'>
              Collection of Feedback For System
            </Header>
          </Segment>
          <Divider hidden/>
          <Header as='h3' textAlign='left'>
            Do you like the system?
          </Header>
          <Radio
            label='Choose this'
            name='radioGroup'
            value='this'
            checked={this.state.value === 'this'}
            onChange={this.handleChange}
          />
          <Radio
            label='Or that'
            name='radioGroup'
            value='that'
            checked={this.state.value === 'that'}
            onChange={this.handleChange}
          />
          <Button icon labelPosition='left' color="green" disabled={this.state.likeGreen} compact={true}>
            <Icon name='thumbs up outline' />
            Yes
          </Button>
          <Button icon labelPosition='left' color="red" disabled={this.state.likeRed} compact={true}>
            <Icon name='thumbs down outline' />
            No
          </Button>
          <Divider />
        </Container>
      </Layout>
    );
  }
}

export default FeedbackPage;
