import React, { Component } from 'react';
import { Button, Header, Table, Statistic, Rating, Grid, Message,
   Divider, Container, Icon, Label } from 'semantic-ui-react';
import factory from '../ethereum/factory';
import credentials from '../ethereum/credentials';
import Question from '../ethereum/question';
import Layout from '../components/Layout';
import {Link, Router} from '../routes';
import web3 from '../ethereum/web3';
import {search} from './search';

class HomePage extends Component {
    static async getInitialProps (props) {
        // Retrieve the Ethereum Account from MetaMask
        const accounts = await web3.eth.getAccounts();
        if (await credentials.methods.getBinding(accounts[0]).call() === ""){
            Router.pushRoute(`/`); // Automatic redirect the user to Login or Sign Up
        }

        // Retrieve an array of addresses of questions deployed
        let deployedQuestions = await factory.methods.getDeployedQuestions().call();
        let deployedQuestionsCount = deployedQuestions.length;

        // Retrieve the summaries of all deployed questions
        let questions = [];
        let summaries = [];
        let numOfAnswers = [];
        let searchItem;
        //filter the questions based on search value
        if (props.query.value != undefined && props.query.value != 'favicon.ico') {
            searchItem = decodeURIComponent(props.query.value);
            deployedQuestions = await search(searchItem,deployedQuestions);
            deployedQuestionsCount = deployedQuestions.length;
        }

        for(let i=0; i<deployedQuestionsCount; i++) {
          questions[i] = Question(deployedQuestions[i]);
          summaries[i] = await questions[i].methods.getSummary().call();
          let answerList = await questions[i].methods.getAnswerList().call();
          numOfAnswers[i] = answerList.length;
        }
        return { accounts, deployedQuestions, summaries, numOfAnswers };
    }

    renderQuestionHeader() {
      return(
        <Table.Row>
          <Table.HeaderCell textAlign='center' width={2}><span style={{fontSize: 15, color: '#6A737C'}}>Current Phase</span></Table.HeaderCell>
          <Table.HeaderCell textAlign='center' width={2}><span style={{fontSize: 15, color: '#6A737C'}}>Answers Submitted</span></Table.HeaderCell>
          <Table.HeaderCell textAlign='center' width={2}><span style={{fontSize: 15, color: '#6A737C'}}>Reward</span></Table.HeaderCell>
          <Table.HeaderCell textAlign='center'><span style={{fontSize: 15, color: '#6A737C'}}>Question Title</span></Table.HeaderCell>
        </Table.Row>
      );
    }

    // Renders out the list of question
    renderQuestionList() {
        return this.props.summaries.map((summary, index) => {
            const questionState = summary[4];
            const tagList = summary[8].split(", ");
            return <Table.Row key={index}>
              <Table.Cell textAlign='center' width={2}>
                  {
                    questionState == 0 ? <Statistic size='mini' color='red'>
                                    <Statistic.Value><span
                                        style={{fontSize: 15, color: '#6A737C'}}> Answering
                                                    </span></Statistic.Value>
                                    <Statistic.Label><span style={{fontSize: 15, color: '#6A737C'}}>Phase</span></Statistic.Label>
                                </Statistic>
                              : questionState == 1 ?
                              <Statistic size='mini' color='red'>
                                    <Statistic.Value><span
                                        style={{fontSize: 15, color: '#6A737C'}}> Voting
                                                    </span></Statistic.Value>
                                    <Statistic.Label><span style={{fontSize: 15, color: '#6A737C'}}>Phase</span></Statistic.Label>
                                </Statistic>
                              :<Statistic size='mini' color='red'>
                                    <Statistic.Value><span
                                        style={{fontSize: 15, color: '#6A737C'}}> Rewarded
                                                    </span></Statistic.Value>
                                    <Statistic.Label><span style={{fontSize: 15, color: '#6A737C'}}>Phase</span></Statistic.Label>
                                </Statistic>
                  }
                </Table.Cell>
                <Table.Cell textAlign='center' width={2}>
                    <Statistic size='mini' color='red'>
                        <Statistic.Value><span style={{fontSize: 15, color: '#6A737C'}}>{this.props.numOfAnswers[index]}</span></Statistic.Value>
                        <Statistic.Label><span style={{fontSize: 15, color: '#6A737C'}}>answer(s)</span></Statistic.Label>
                    </Statistic>
                </Table.Cell>
                <Table.Cell textAlign='center' width={2}>
                    <Statistic size='mini' color='red'>
                        <Statistic.Value><span style={{fontSize: 15, color: '#6A737C'}}>{summary[2]}</span></Statistic.Value>
                        <Statistic.Label><span style={{fontSize: 15, color: '#6A737C'}}>ether(s)</span></Statistic.Label>
                    </Statistic>
                </Table.Cell>
                <Table.Cell textAlign='left'>
                    <Grid.Row textAlign='left'>
                        <span style={{fontSize: 18, color: '#6A737C', cursor: 'pointer'}} onClick={() => Router.pushRoute(`/questions/${this.props.deployedQuestions[index]}`)}><a>{summary[0]}</a></span>
                    </Grid.Row>
                    {this.renderTag(tagList)}
                </Table.Cell>
            </Table.Row>
      });
    }

    // Render each tag individually for each question
    renderTag(tagList){
      const tagColours = ["red", "orange", "yellow", "olive", "green"];
      return tagList.map((tag, index) => {
        let id = index % 5;
        return(
          <Label as='a' tag size={"mini"} key={index} color={'red'}>
            {tag}
          </Label>
        )
      });
    }

    render() {
        return (
            <Layout>
                <Header as='h2' textAlign='center'>
                    Questions Posted
                </Header>
                <Divider hidden/>
                <Container>
                  <Table>
                    <Table.Header>
                      {this.renderQuestionHeader()}
                    </Table.Header>
                    <Table.Body>
                      {this.renderQuestionList()}
                    </Table.Body>
                  </Table>
                  <Divider hidden/>
                  <div style={{ marginTop: 20 }}>Found {this.props.summaries.length} Item(s).</div>
                </Container>
            </Layout>
        );
    }
}

export default HomePage;
