import React, { Component } from 'react';
import Layout from '../components/Layout';
import { Container, Segment, Header, Divider,
   Button, Icon, TextArea, Modal, Loader, Message, Form } from "semantic-ui-react";
import web3 from '../ethereum/web3';
import factory from '../ethereum/factory';
import Profile from '../ethereum/profile';
import credentials from '../ethereum/credentials';
import feedback from '../ethereum/feedback';
import {Router} from '../routes';

class FeedbackPage extends Component {
  constructor(props) {
    super(props);
    this.state = {
      like: "",
      usefulness: "",
      easy: "",
      comments:"",
      loading: false,
      errorMessage: ""
    };
  }

  static async getInitialProps(props) {
    // Retrieve the Ethereum Account from MetaMask
    const accounts = await web3.eth.getAccounts();
    if (await credentials.methods.getBinding(accounts[0]).call() === ""){
        Router.pushRoute(`/`); // Automatic redirect the user to Login or Sign Up
    }

    // Get Account Type
    const profileAddress = await factory.methods.getProfile(accounts[0]).call();
    const profile = Profile(profileAddress);
    let accountType = await profile.methods.getAccountType().call();
    if (accountType == 0) {
      accountType = "Admin"
    } else {
      accountType = "User"
    }
    return { accountType }
  }

  onLike = () => {
    this.setState({ like: true});
  }

  onDislike = () => {
    this.setState({ like: false});
  }

  onUseful = () => {
    this.setState({ usefulness: true});
  }

  onNotUseful = () => {
    this.setState({ usefulness: false});
  }

  onEasy = () => {
    this.setState({ easy: true});
  }

  onNotEasy = () => {
    this.setState({ easy: false});
  }

  checkFieldEmpty(field){
    return field === "" ? false : true;
  }

  // Submit the question to be stored on the Ethereum Blockchain
  onSubmit = async () => {
      this.setState({ loading: true, errorMessage: '' });
      // Ensures all fields are filled
      if (this.checkFieldEmpty(this.state.like) && this.checkFieldEmpty(this.state.usefulness) && this.checkFieldEmpty(this.state.easy)){
        try {
            const accounts = await web3.eth.getAccounts();
            // Create a new post
            await feedback.methods
            .updateFeedback(this.state.like, this.state.usefulness, this.state.easy, this.state.comments)
            .send({
                from: accounts[0]
            });
            Router.pushRoute('/home'); // Automatic redirect the user.
        } catch (err) {
            this.setState({ errorMessage: err.message });
        }
        this.setState({ loading: false });
      } else {
        this.setState({ errorMessage: "Required Field(s) Empty" });
        this.setState({ loading: false });
      }
  };

  render() {
    return(
      <Layout accountType={this.props.accountType}>
        <Container style={{height:"100vh"}}>
          <Divider hidden/>
          <Segment raised textAlign='left'>
            <Header as='h2' textAlign='center'>
              Collection of Feedback For System
            </Header>
          </Segment>
          <Divider hidden/>
          <Header as='h3' textAlign='left'>
            Do you like the system?
          </Header>
          <p>Selected Choice: <b>{this.state.like.toString()}</b>
          </p>
          <Button icon labelPosition='left' color="green" compact={true} onClick={this.onLike}>
            <Icon name='thumbs up outline' />
            Yes
          </Button>
          <Button icon labelPosition='left' color="red" compact={true} onClick={this.onDislike}>
            <Icon name='thumbs down outline' />
            No
          </Button>
          <Header as='h3' textAlign='left'>
            Do you find the system useful?
          </Header>
          <p>
          Selected Choice: <b>{this.state.usefulness.toString()}</b>
          </p>
          <Button icon labelPosition='left' color="green" compact={true} onClick={this.onUseful}>
            <Icon name='thumbs up outline' />
            Yes
          </Button>
          <Button icon labelPosition='left' color="red" compact={true} onClick={this.onNotUseful}>
            <Icon name='thumbs down outline' />
            No
          </Button>
          <Header as='h3' textAlign='left'>
            Do you find the system easy to use?
          </Header>
          <p>
          Selected Choice: <b>{this.state.easy.toString()}</b>
          </p>
          <Button icon labelPosition='left' color="green" compact={true} onClick={this.onEasy}>
            <Icon name='thumbs up outline' />
            Yes
          </Button>
          <Button icon labelPosition='left' color="red" compact={true} onClick={this.onNotEasy}>
            <Icon name='thumbs down outline' />
            No
          </Button>
          <Header as='h3' textAlign='left'>
            Any suggestions for improvments or other comments?
          </Header>
          <Form error={!!this.state.errorMessage} style={{ marginTop: '10px' }}>
            <Form.Field>
              <TextArea placeholder='Tell us more' style={{width:"100%", height:"20%"}}
                value={this.state.comments}
                onChange={event => this.setState({ comments: event.target.value })}/>
            </Form.Field>
            <Message error header="Oops!" content={this.state.errorMessage}/>
          </Form>
          <Divider hidden/>
          <Modal open={this.state.loading} trigger={<Button primary onClick={this.onSubmit}>Submit Feedback</Button>} basic size='small'>
            <Header content='Submitting Feedback' />
            <Modal.Content>
              <p>
                Please Confirm the MetaMask Transaction Request to submit your Feedback to the Block-Chain Network.
                Upon successful submission, you will be redirected to the Home Page. This process might take awhile.
              </p>
              <Loader active inline="centered">
                Loading
              </Loader>
            </Modal.Content>
          </Modal>
        </Container>
      </Layout>
    );
  }
}

export default FeedbackPage;
