import React, { Component } from 'react';
import Layout from '../components/Layout';
import { Container, Segment, Header, Divider, Button, Popup,
   Icon, Dropdown, Table, Modal, Loader, Message, Transition } from "semantic-ui-react";
import {Router} from '../routes';
import { logging } from './logger';
import web3 from '../ethereum/web3';
import factory from '../ethereum/factory';
import Question from '../ethereum/question';
import credentials from '../ethereum/credentials';

// Ensure jsPDF does not import on Server Side
let jsPDF = null;
if (typeof window !== "undefined") {
  import("jspdf").then(module => {
    jsPDF = module.default;
  });
}

class AdminPage extends Component {
  constructor(props) {
    super(props);
    this.state = {
      questionOptions: [],
      questionNumber: '',
      error: false,
      errorMessage: '',
      loading: false,
      userVisible: false,
      questionVisible: false
    };
  }

  static async getInitialProps () {
      const accounts = await web3.eth.getAccounts();
      // Redirect E-Wallet that are not Logged In to the Login Page
      if (await credentials.methods.getBinding(accounts[0]).call() === ""){
          Router.pushRoute(`/`);
      }

      // Retrieve an array of addresses of questions deployed
      let deployedQuestions = await factory.methods.getDeployedQuestions().call();
      let deployedQuestionsCount = deployedQuestions.length;
      let questionAddressTitleBinding = []
      for(let i=0; i<deployedQuestionsCount; i++) {
        let question = Question(deployedQuestions[i]);
        let title = await question.methods.getSummary().call()[0];
        questionAddressTitleBinding.push({
          questionAddress: deployedQuestions[i],
          title: title
        })
      }

      // Retrieve an array of EthWallets and Email Addresses
      let ethWallets = await factory.methods.getEthWallets().call();
      let ethWalletEmailAddressBinding = [];
      for (let i=0; i < ethWallets.length; i++) {
        let emailAddress = await credentials.methods.getBinding(ethWallets[i]).call();
        let status = await credentials.methods.emailNotBlackList(emailAddress).call();
        ethWalletEmailAddressBinding.push({
          ethWallet: ethWallets[i],
          emailAddress: emailAddress,
          status: status
        })
      }

      return { accounts, deployedQuestionsCount, deployedQuestions, ethWalletEmailAddressBinding, questionAddressTitleBinding }
  }

  async componentWillMount() {
    // Retrieve the Ethereum Account from MetaMask
    const accounts = await web3.eth.getAccounts();
    // Redirect E-Wallet that are not Logged In to the Login Page
    if (await credentials.methods.getBinding(accounts[0]).call() === ""){
        Router.pushRoute(`/`); // Automatic redirect the user to Login or Sign Up
    }
  }

  async componentDidMount() {
    let questionOptions = [];
    for (let i = 0; i < this.props.deployedQuestionsCount; i++) {
        let question = Question(this.props.deployedQuestions[i])
        let summary = await question.methods.getSummary().call();
        questionOptions.push({
            key: i+1,
            text: summary[0],
            value: i+1
        });
    }
    this.setState( { questionOptions: questionOptions });
  }

  export = async () => {
    // Check that a question must be selected
    if (this.state.questionNumber != "") {
      // Initialise the PDF document to write
      let lMargin=15; //left margin in mm
      let rMargin=15; //right margin in mm
      let pdfInMM=210;  // width of A4 in mm
      let doc = new jsPDF("p","mm","a4");

      // Retrieve all the data of selected question
      let questionNumber = Number(this.state.questionNumber) - 1;
      let question = Question(this.props.deployedQuestions[questionNumber])
      let summary = await question.methods.getSummary().call();
      let answerList = await question.methods.getAnswerList().call();
      let questionTitle = "Question: " + summary[1];

      // Writing to the PDF document
      let lines = doc.splitTextToSize(questionTitle, (pdfInMM-lMargin-rMargin));
      doc.text(lMargin,20,lines);
      doc.setLineWidth(0.5)
      doc.line(lMargin, 20+lines.length+8, 195, 20+lines.length+8)

      // If images are uploaded
      if (summary[5].length != 0) {
          summary[6].map((imageName, index) => {
            let link = "https://ipfs.io/ipfs/"+summary[5][index]
            doc.text(lMargin,20+lines.length+15,"Link(s) of Image(s) Uploaded For Question:")
            lines = doc.splitTextToSize(link, (pdfInMM-lMargin-rMargin));
            doc.text(lMargin,45,lines);
          })
          doc.setLineWidth(0.5)
          doc.line(lMargin, 20+lines.length+35, 195, 20+lines.length+35)
          doc.text(lMargin, 20+lines.length+45, "Answers:");
      } else {
        doc.text(lMargin, 20+lines.length+15, "Answers:");
      }
      let j = 75;
      for (let i = 0; i < answerList.length; i++) {
        let answer = "(" + (i+1) + ") " + answerList[i][0];
        lines = doc.splitTextToSize(answer, (pdfInMM-lMargin-rMargin));
        // Creates a new page for every 10 answers
        if (i % 10 == 0 && i != 0) {
          doc.addPage();
          j = 30;
        }
        doc.text(lMargin, j, lines);
        j = j + 10;
      }
      doc.save("Answer.pdf");
    } else {
      console.log("Please select a question")
    }
  }

  getUserTableData = () => {
    return this.props.ethWalletEmailAddressBinding.map((item, index) => {
      return <Table.Row key={index}>
        <Table.Cell>{item.ethWallet}</Table.Cell>
        <Table.Cell>{item.emailAddress}</Table.Cell>
        <Table.Cell>{item.status ? "Blacklisted" : "Active"}</Table.Cell>
        <Table.Cell>
        {
          item.status ? <Button onClick={() => this.unblacklist(item.emailAddress)} color="green" size="mini" icon>
            <Icon name="undo" />
          </Button> :
          <Button onClick={() => this.blacklist(item.emailAddress)} color="red" size="mini" icon>
            <Icon name="delete" />
          </Button>
        }
        </Table.Cell>
      </Table.Row>;
    })
  }

  blacklist = async (emailAddress) => {
    this.setState({ loading: true, errorMessage: ''});
    try {
        const accounts = await web3.eth.getAccounts();
        // Create a new post
        await credentials.methods
        .blacklistEmail(emailAddress)
        .send({
            from: accounts[0]
        });
        Router.pushRoute('/admin'); // Automatic redirect the user.
    } catch (err) {
        this.setState({ errorMessage: err.message });
        this.setState({ error: true})
    }
    this.setState({ loading: false})
  }

  unblacklist = async (emailAddress) => {
    this.setState({ loading: true, errorMessage: ''});
    try {
        const accounts = await web3.eth.getAccounts();
        // Create a new post
        await credentials.methods
        .unBlacklistEmail(emailAddress)
        .send({
            from: accounts[0]
        });
    } catch (err) {
        this.setState({ errorMessage: err.message });
        this.setState({ error: true})
    }
    this.setState({ loading: false})
  }

  getQuestionTableData = () => {
    return this.props.questionAddressTitleBinding.map((item, index) => {
      return <Table.Row key={index}>
        <Table.Cell>{item.item.questionAddress}</Table.Cell>
        <Table.Cell>{item.title}</Table.Cell>
        <Table.Cell>
        <Button onClick={() => this.deleteQuestion(item.questionAddress)} color="red" size="mini" icon>
          <Icon name="delete" />
        </Button>
        </Table.Cell>
      </Table.Row>;
    })
  }

  deleteQuestion = async (questionAddress) => {
    this.setState({ loading: true, errorMessage: ''});
    try {
        const accounts = await web3.eth.getAccounts();

        // Create a new post
        await credentials.methods
        .unBlacklistEmail(emailAddress)
        .send({
            from: accounts[0]
        });
    } catch (err) {
        this.setState({ errorMessage: err.message });
        this.setState({ error: true})
    }
    this.setState({ loading: false})
  }

  toggleUserTableVisibility = () =>
    this.setState((prevState) => ({ userVisible: !prevState.userVisible }))

  toggleQuestionTableVisibility = () =>
    this.setState((prevState) => ({ questionVisible: !prevState.questionVisible }))

  render() {
    return (
      <Layout>
        <Container style={{height:"100vh"}}>
          <Divider hidden/>

          <Segment raised textAlign='left'>
            <Header as='h2' textAlign='center'>
              Exporting Questions & Answers As PDF
            </Header>
          </Segment>
          <Divider hidden/>
          <p style={{fontSize:'16px'}}>
            Select the question to export the question and it's corresponding answers
          </p>
          <Dropdown clearable
                    options={this.state.questionOptions}
                    selection
                    placeholder='Select the question to export'
                    value={this.state.questionNumber}
                    onChange={ (e, { value }) => this.setState({ questionNumber: value })}/>
          <Button icon labelPosition='left' color="red" onClick={this.export} style={{left:"20px"}}>
            <Icon name='download'/>
            Export As PDF
          </Button>
          <Divider hidden/>
          <Segment raised textAlign='center'>
            <b style={{fontSize:"20px"}}>Blacklisting User</b>
            <Popup
              trigger={
                <Button
                  icon={this.state.userVisible ? "minus" : "plus"}
                  onClick={this.toggleUserTableVisibility}
                  floated="right"
                  size="mini"
                />
              }
              content={"Click to expand/close"}
              inverted/>
          </Segment>
          <Divider hidden/>

          <Transition visible={this.state.userVisible} animation='scale' duration={500}>
            <Segment>
              <Header>Registered Users</Header>
                <Table compact celled>
                  <Table.Header fullWidth>
                    <Table.Row>
                      <Table.HeaderCell width={2}>Ethereum Wallet</Table.HeaderCell>
                      <Table.HeaderCell width={3}>Email</Table.HeaderCell>
                      <Table.HeaderCell width={2}>Status</Table.HeaderCell>
                      <Table.HeaderCell width={2}>Actions</Table.HeaderCell>
                    </Table.Row>
                  </Table.Header>
                  <Table.Body>{this.getUserTableData()}</Table.Body>
                  <Table.Footer fullWidth>
                    <Table.Row>
                      <Table.HeaderCell colSpan={5} />
                    </Table.Row>
                  </Table.Footer>
                </Table>
            </Segment>
          </Transition>
          <Divider hidden/>
          <Segment raised textAlign='center'>
            <b style={{fontSize:"20px"}}>Removing Question</b>
            <Popup
              trigger={
                <Button
                  icon={this.state.questionVisible ? "minus" : "plus"}
                  onClick={this.toggleQuestionTableVisibility}
                  floated="right"
                  size="mini"
                />
              }
              content={"Click to expand/close"}
              inverted/>
          </Segment>
          <Divider hidden/>

          <Transition visible={this.state.questionVisible} animation='scale' duration={500}>
            <Segment>
              <Header>Questions Posted</Header>
                <Table compact celled>
                  <Table.Header fullWidth>
                    <Table.Row>
                      <Table.HeaderCell width={2}>Question Address</Table.HeaderCell>
                      <Table.HeaderCell width={3}>Title</Table.HeaderCell>
                      <Table.HeaderCell width={2}>Actions</Table.HeaderCell>
                    </Table.Row>
                  </Table.Header>
                  <Table.Body>{this.getQuestionTableData()}</Table.Body>
                  <Table.Footer fullWidth>
                    <Table.Row>
                      <Table.HeaderCell colSpan={5} />
                    </Table.Row>
                  </Table.Footer>
                </Table>
            </Segment>
          </Transition>
          <Divider hidden/>

          <Modal open={this.state.loading} basic size='small'>
            <Header content='Performing Admin Actions' />
            <Modal.Content>
              <p>
                Please Confirm the MetaMask Transaction Request. This process might take awhile.
              </p>
              <Loader active inline="centered">
                Loading
              </Loader>
            </Modal.Content>
          </Modal>
          <Message error header="Oops!" content={this.state.errorMessage} hidden={!this.state.error}/>
        </Container>
      </Layout>
    )
  }
}

export default AdminPage;
