// Validate Email Address Format
const validate = (emailAddress) => {
  if (emailAddress === "admin") {
    return true;
  } else {
    const re = /\S+@e\.ntu\.edu\.sg$/;
    return re.test(emailAddress);
  }
};

export { validate };
