// Ensure jsPDF does not import on Server Side
let jsPDF = null;
if (typeof window !== "undefined") {
  import("jspdf").then(module => {
    jsPDF = module.default;
  });
}

const exportQuestion = async(summary, answerList) => {
  // Initialise the PDF document to write
  let lMargin=15; //left margin in mm
  let rMargin=15; //right margin in mm
  let pdfInMM=210;  // width of A4 in mm
  let doc = new jsPDF("p","mm","a4");

  let questionTitle = "Question: " + summary[1];

  // Writing to the PDF document
  let lines = doc.splitTextToSize(questionTitle, (pdfInMM-lMargin-rMargin));
  doc.text(lMargin,20,lines);
  doc.setLineWidth(0.5)
  doc.line(lMargin, 20+lines.length+8, 195, 20+lines.length+8)

  // If images are uploaded
  if (summary[5].length != 0) {
      summary[6].map((imageName, index) => {
        let link = "https://ipfs.io/ipfs/"+summary[5][index]
        doc.text(lMargin,20+lines.length+15,"Link(s) of Image(s) Uploaded For Question:")
        lines = doc.splitTextToSize(link, (pdfInMM-lMargin-rMargin));
        doc.text(lMargin,45,lines);
      })
      doc.setLineWidth(0.5)
      doc.line(lMargin, 20+lines.length+35, 195, 20+lines.length+35)
      doc.text(lMargin, 20+lines.length+45, "Answers:");
  } else {
    doc.text(lMargin, 20+lines.length+15, "Answers:");
  }
  let j = 75;
  for (let i = 0; i < answerList.length; i++) {
    let answer = "(" + (i+1) + ") " + answerList[i][0];
    lines = doc.splitTextToSize(answer, (pdfInMM-lMargin-rMargin));
    // Creates a new page for every 10 answers
    if (i % 10 == 0 && i != 0) {
      doc.addPage();
      j = 30;
    }
    doc.text(lMargin, j, lines);
    j = j + 10;
  }
  let pageCount = doc.internal.getNumberOfPages();
  doc = createFooter(doc, pageCount);
  doc.save("Answer.pdf");
}

const createFooter = (document, pageCount) => {
  for(let i = 0; i < pageCount; i++) {
    document.setPage(i)
    document.setFontSize(13)
    document.setTextColor(150)
    document.text(170, 280, "Page " + doc.internal.getCurrentPageInfo().pageNumber + " of " + pageCount)
  }
  return document
}

export { exportQuestion }
