import React, { Component } from 'react';
import { Button, Checkbox, Form, Container, Divider, Input } from 'semantic-ui-react'
import Layout from '../components/Layout';

class LoginPage extends Component {

  state = {
    walletAddress:'',
    emailAddress:'',
    loading: false
  };

  renderForm() {
    return(
      <Form>
        <Form.Field>
          <label>Ethereum Wallet Address</label>
          <Input
              placeholder='0x573391b457fF7E94622b4CFa6a49Cd9e5ef57346'
              value={this.state.walletAddress}
              onChange={event => this.setState({ walleteAddress: event.target.value})}
           />
        </Form.Field>
        <Form.Field>
          <label>NTU Student Email Address</label>
          <Input
              placeholder='example@e.ntu.edu.sg'
              value={this.state.emailAddress}
              onChange={event => this.setState({ emailAddress: event.target.value})}
          />
        </Form.Field>
        <Button loading={this.state.loading} primary onClick={this.onSubmit}>Submit Question</Button>
      </Form>
    );
  }

  render() {
    return(
      <Layout>
        <Divider hidden/>
        <Container>
          {this.renderForm()}
        </Container>
      </Layout>
    );
  }
}

export default LoginPage;
