import React, { Component } from 'react';
import { Menu, Icon, Input, Segment, Sticky, Container, Button, Dropdown, Grid } from 'semantic-ui-react';
import { Link, Router } from '../routes';

class DesktopHeader extends Component {
  constructor(props) {
    super(props);
    this.state = {
      value:''
    }
    this.handleChange = this.handleChange.bind(this);
    this.keyPress = this.keyPress.bind(this);
   }

  handleChange(e) {
    this.setState({ value: e.target.value });
   }

  keyPress(e){
    if(e.keyCode == 13){
     if (this.state.value!='')
          Router.pushRoute(`/${'search/'+encodeURIComponent(this.state.value)}`);
    }
  }

  render() {
    return (
      <React.Fragment>
        <Segment inverted style={{marginBottom:'0px', fontSize:'20px', borderRadius:'0px', border:'0px'}} as='h1' textAlign='center'>
          <Grid centered>
            <Grid.Column width={14} textAlign="center">
              <Icon name='ethereum' />Block-Chain Based Question Answering System
            </Grid.Column>
            <Grid.Column width={2}>
              <Button compact floated='right' inverted color='orange' onClick={()=>{Router.pushRoute('/')}}><Icon name='user' circular size='small'/>Log Out</Button>
            </Grid.Column>
          </Grid>
        </Segment>
        <Sticky context={this.props.contextRef}>
        <Grid>
          <Grid.Column>
            <Container>
            </Container>
          </Grid.Column>
          <Grid.Column width={13}>
            <Menu inverted size='massive' style={{marginTop:'0px', borderRadius:'0px', border:'0px'}}>
              <Link route="/home">
                <Menu.Item
                  name="Home"
                  active = {this.props.page === 'Home'}
                  color='orange'
                />
              </Link>
              <Link route="/about">
                <Menu.Item
                  name="About"
                  active = {this.props.page === 'About'}
                  color='orange'
                />
              </Link>
              <Link route='/userProfile'>
                <Menu.Item
                  name="Profile"
                  active = {this.props.page === 'Profile'}
                  color='orange'
                />
              </Link>
              <Link route="/new">
                <Menu.Item
                  name="Ask Question"
                  active = {this.props.page === 'New'}
                  color='orange'
                />
              </Link>
              <Link route="/feedback">
                <Menu.Item
                  name="Feedback"
                  active = {this.props.page === 'Feedback'}
                  color='orange'
                />
              </Link>
              {this.props.accountType == "Admin" ? <Link route="/admin">
                                                    <Menu.Item
                                                      name="Admin Options"
                                                      active = {this.props.page === 'Admin'}
                                                      color='orange'
                                                      />
                                                   </Link>
                                                 : null }
              <Menu.Menu position="right">
                <Menu.Item>
                  <Input icon={<Icon name="search" link onClick={() => {
                   if (this.state.value!='') {
                     Router.pushRoute(`/${'search/'+encodeURIComponent(this.state.value)}`);
                   }
                  }}/>}
                  placeholder="Search..."
                  onKeyDown={this.keyPress}
                  onChange={this.handleChange}
                   />
                </Menu.Item>
              </Menu.Menu>
            </Menu>
          </Grid.Column>
          <Grid.Column>
          </Grid.Column>
        </Grid>
        </Sticky>
        {this.props.children}
      </React.Fragment>
    );
  }
}

export default DesktopHeader;
