import Web3 from 'web3'; // Web3 with capital W is the constructor.
// New instance of Web3 with the provider that is automatically provided to us by MetaMask inside the browser.
// This makes the assumption that the user has MetaMask.

var web3; // if - We execute inside the browser and MetaMask is available

if (typeof window !== 'undefined' && typeof window.web3 !== 'undefined') {
  web3 = new Web3(window.web3.currentProvider);
} else {
  // We are on the server *OR* the user is not running MetaMask.
  // Create our own provider:
  var provider = new Web3.providers.HttpProvider( // Pass the URL of some remote node that we have excess to (such as Infura node).
  //'http://127.0.0.1:8543'
  'http://192.168.86.23:8543' //'http://192.168.1.171:8543'
  //'http://172.21.148.184:8543'
  //'http://10.27.243.45:8543'
  //'http://localhost:8545'
  );
  web3 = new Web3(provider);
}

export default web3;
