import React, { Component } from 'react';
import { Menu, Icon, Input, Segment, Sticky } from 'semantic-ui-react';
import { Link, Router } from '../routes';

class Header extends Component {
  constructor(props) {
      super(props);
      this.state = {value:''}

      this.handleChange = this.handleChange.bind(this);
      this.keyPress = this.keyPress.bind(this);
   }

  handleChange(e) {
      this.setState({ value: e.target.value });
   }

  keyPress(e){
      if(e.keyCode == 13){
         if (this.state.value!='')
              Router.pushRoute(`/${'search/'+encodeURIComponent(this.state.value)}`);
      }
  }

  render() {
    return (
      <React.Fragment>
        <Segment inverted style={{marginBottom:'0px', fontSize:'20px', borderRadius:'0px', border:'0px'}} as='h1' textAlign='center'>
          <Icon name='ethereum' />Block-Chain Based Question Answering System
        </Segment>
        <Sticky context={this.props.contextRef}>
          <Menu inverted size='massive' style={{marginTop:'0px', borderRadius:'0px', border:'0px'}}>
            <Link route="/home">
              <Menu.Item color={'blue'}
                name="Home"
              />
            </Link>
            <Link route="/about">
              <Menu.Item
                name="About"
              />
            </Link>
            <Link route='/userProfile'>
              <Menu.Item
                name="Profile"
              />
            </Link>
            <Link route="/new">
              <Menu.Item
                name="Create New Question"
              />
            </Link>
            <Menu.Menu position="right">
              <Menu.Item>
                <Input icon={<Icon name="search" link onClick={() => {
                 if (this.state.value!='') {
                   Router.pushRoute(`/${'search/'+encodeURIComponent(this.state.value)}`);
                 }
                }}/>}
                placeholder="Search..."
                onKeyDown={this.keyPress}
                onChange={this.handleChange}
                 />
              </Menu.Item>
            </Menu.Menu>
          </Menu>
        </Sticky>
      </React.Fragment>
    );
  }
}

export default Header;
