import React, { Component } from 'react';
import { Button, Header, Table, Statistic, Rating, Grid, Loader,
   Divider, Container, Icon, Label, Rail, Progress, Segment, Modal } from 'semantic-ui-react';
import _ from 'lodash';
import factory from '../ethereum/factory';
import credentials from '../ethereum/credentials';
import Question from '../ethereum/question';
import Profile from '../ethereum/profile';
import Layout from '../components/Layout';
import {Link, Router} from '../routes';
import web3 from '../ethereum/web3';
import {search} from './search';

class HomePage extends Component {
  constructor(props) {
    super(props);
    this.state = {
      column: null,
      direction: null,
      leaderboardData: [],
      login: false,
      loading: true,
      accountType: null,
      tags: []
    };
  }

  // Function to handle the sorting of leaderboard
  handleSort = (clickedColumn) => () => {
    const { column, leaderboardData, direction } = this.state

    if (column !== clickedColumn) {
      this.setState({
        column: clickedColumn,
        leaderboardData: _.sortBy(leaderboardData, [clickedColumn]),
        direction: 'ascending',
      })
      return
    }

    this.setState({
      leaderboardData: leaderboardData.reverse(),
      direction: direction === 'ascending' ? 'descending' : 'ascending',
    })
  }

  static async getInitialProps(props) {
      // Retrieve an array of addresses of questions deployed
      let deployedQuestions = await factory.methods.getDeployedQuestions().call();
      let deployedQuestionsCount = deployedQuestions.length;

      // Retrieve the summaries of all deployed questions
      let questions = [];
      let summaries = [];
      let numOfAnswers = [];
      let searchItem;
      //filter the questions based on search value
      if (props.query.value != undefined && props.query.value != 'favicon.ico') {
          searchItem = decodeURIComponent(props.query.value);
          deployedQuestions = await search(searchItem,deployedQuestions);
          deployedQuestionsCount = deployedQuestions.length;
      }
      for(let i=0; i<deployedQuestionsCount; i++) {
        questions[i] = Question(deployedQuestions[i]);
        let summary = await questions[i].methods.getSummary().call();
        // Filter out flagged questions
        if (!summary[9]) {
          summaries[i] = summary;
          let answerList = await questions[i].methods.getAnswerList().call();
          numOfAnswers[i] = answerList.length;
        } else {
          // Remove question from list of deployed questions
          deployedQuestions.splice(i, 1);
          deployedQuestionsCount--;
          i--;
        }
      }
      return { deployedQuestions, summaries, numOfAnswers };
  }

  async componentDidMount() {
    // Retrieve the Ethereum Account from MetaMask
    const accounts = await web3.eth.getAccounts();
    let login = false;
    if (await credentials.methods.getBinding(accounts[0]).call() === "") {
    } else {
      login = true;
      // Get Account Type
      let profileAddress = await factory.methods.getProfile(accounts[0]).call();
      let profile = Profile(profileAddress);
      let accountType = await profile.methods.getAccountType().call();
      accountType == 0 ? accountType = "Admin" : accountType = "User"
      // Retireve an array of addresses of ethWallets registered
      let ethWallet = await factory.methods.getEthWallets().call();
      let accountPointMapping = [];

      // Populate data required for leaderboard
      for (let i=0; i<ethWallet.length; i++) {
        profileAddress = await factory.methods.getProfile(ethWallet[i]).call();
        profile = Profile(profileAddress);
        let points = await profile.methods.getPoints().call();
        accountPointMapping.push({
          account: ethWallet[i],
          points: parseInt(points)
        });
      }

      // Get the top 10 used tags
      let tags = {};
      this.props.summaries.forEach(summary =>{
        summary[8].forEach(tag => {
          if (tags[tag] == undefined) {
            tags[tag] = 1;
          } else {
            tags[tag] = tags[tag] + 1;
          }
        })
      })
      let sortedTags = Object.keys(tags).map(function(key) {
        return [key, tags[key]];
      });

      // Sort the array based on the second element
      sortedTags.sort(function(first, second) {
        return second[1] - first[1];
      });
      tags = [];

      // Create a new array with only the first 10 items
      sortedTags.slice(0, 10).forEach(tag => {
        tags.push(tag[0])
      })

      // Set the states required for the Leaderboard and ensure it is descending by default
      this.setState( { leaderboardData: _.sortBy(accountPointMapping, ['points']).reverse() });
      this.setState( { login: login });
      this.setState( { accountType: accountType });
      this.setState({ tags: tags });
    }
    this.setState( { loading: false });
  }

  renderQuestionHeader() {
    return(
      <Table.Row>
        <Table.HeaderCell textAlign='center' width={2}><span style={{fontSize: 15, color: '#6A737C'}}>Current Phase</span></Table.HeaderCell>
        <Table.HeaderCell textAlign='center' width={2}><span style={{fontSize: 15, color: '#6A737C'}}>Answers Submitted</span></Table.HeaderCell>
        <Table.HeaderCell textAlign='center' width={2}><span style={{fontSize: 15, color: '#6A737C'}}>Reward</span></Table.HeaderCell>
        <Table.HeaderCell textAlign='center'><span style={{fontSize: 15, color: '#6A737C'}}>Question Title</span></Table.HeaderCell>
      </Table.Row>
    );
  }

  // Renders out the list of question
  renderQuestionList() {
      return this.props.summaries.map((summary, index) => {
          const questionState = summary[4];
          const tagList = summary[8];
          return <Table.Row key={index}>
            <Table.Cell textAlign='center' width={2}>
                {
                  questionState == 0 ? <Statistic size='mini' color='red'>
                                  <Statistic.Value><span
                                      style={{fontSize: 15, color: '#6A737C'}}> Answering
                                                  </span></Statistic.Value>
                                  <Statistic.Label><span style={{fontSize: 15, color: '#6A737C'}}>Phase</span></Statistic.Label>
                              </Statistic>
                            : questionState == 1 ?
                            <Statistic size='mini' color='red'>
                                  <Statistic.Value><span
                                      style={{fontSize: 15, color: '#6A737C'}}> Voting
                                                  </span></Statistic.Value>
                                  <Statistic.Label><span style={{fontSize: 15, color: '#6A737C'}}>Phase</span></Statistic.Label>
                              </Statistic>
                            :<Statistic size='mini' color='red'>
                                  <Statistic.Value><span
                                      style={{fontSize: 15, color: '#6A737C'}}> Rewarded
                                                  </span></Statistic.Value>
                                  <Statistic.Label><span style={{fontSize: 15, color: '#6A737C'}}>Phase</span></Statistic.Label>
                              </Statistic>
                }
              </Table.Cell>
              <Table.Cell textAlign='center' width={2}>
                  <Statistic size='mini' color='red'>
                      <Statistic.Value><span style={{fontSize: 15, color: '#6A737C'}}>{this.props.numOfAnswers[index]}</span></Statistic.Value>
                      <Statistic.Label><span style={{fontSize: 15, color: '#6A737C'}}>answer(s)</span></Statistic.Label>
                  </Statistic>
              </Table.Cell>
              <Table.Cell textAlign='center' width={2}>
                  <Statistic size='mini' color='red'>
                      <Statistic.Value><span style={{fontSize: 15, color: '#6A737C'}}>{summary[2]*1e-4}</span></Statistic.Value>
                      <Statistic.Label><span style={{fontSize: 15, color: '#6A737C'}}>EQT(s)</span></Statistic.Label>
                  </Statistic>
              </Table.Cell>
              <Table.Cell textAlign='left'>
                  <Grid.Row textAlign='left'>
                      <span style={{fontSize: 18, color: '#6A737C', cursor: 'pointer'}} onClick={() => Router.pushRoute(`/questions/${this.props.deployedQuestions[index]}`)}><a>{summary[0]}</a></span>
                  </Grid.Row>
                  {this.renderTag(tagList)}
              </Table.Cell>
          </Table.Row>
    });
  }

  // Render each tag individually for each question
  renderTag(tagList) {
    const tagColours = ["red", "olive", "blue", "olive", "green"];
    return tagList.map((tag, index) => {
      let id = index % 5;
      return(
        <Label as='a' onClick={() => Router.pushRoute(`/${'search/'+encodeURIComponent(tag)}`)}
          tag size={"mini"} key={index} color={tagColours[id]}>
          {tag}
        </Label>
      )
    });
  }

  renderLeaderBoard() {
    const { column, data, direction } = this.state
    return (
      <Rail position='left' style={{top:"22%", right:"111%"}}>
        <Segment style={{width:"155%"}}>
          <Header as='h3' textAlign="center">
            <Icon name="chess king"/>Leaderboard
          </Header>
          <p style={{ textAlign:'center'}}>
            <b>Current Registered User(s):</b> {this.state.leaderboardData.length}
          </p>
          <Table basic='very' celled collapsing sortable>
            <Table.Header>
              <Table.Row>
                <Table.HeaderCell>Account</Table.HeaderCell>
                <Table.HeaderCell
                  sorted={column === 'points' ? direction : null}
                  onClick={this.handleSort('points')}>
                Point(s)
                </Table.HeaderCell>
              </Table.Row>
            </Table.Header>
            <Table.Body>
              {this.renderUserList()}
            </Table.Body>
          </Table>
        </Segment>
      </Rail>
    );
  }

  renderTopTags() {
    return (
      <Rail position='right' style={{top:"22%", left:"95%"}}>
        <Segment style={{width:"155%"}}>
          <Header as='h3' textAlign="center">
            <Icon name="tags"/>Popular Tags
          </Header>
          {this.renderTagIcon()}
        </Segment>
      </Rail>
    );
  }

  renderTagIcon() {
    return this.state.tags.map((tag, index) => {
      return(
        <Label as='a' onClick={() => Router.pushRoute(`/${'search/'+encodeURIComponent(tag)}`)}
          tag size="mini" key={index} color="red">
          {tag}
        </Label>
      )
    });
  }

  renderUserList() {
    return this.state.leaderboardData.map((item, index) => {
      return (
        <Table.Row key={index}>
          <Table.Cell textAlign='center'>
            <p style={{fontSize:"12px"}}>{item.account}</p>
          </Table.Cell>
          <Table.Cell textAlign='center'>
            <p style={{fontSize:"12px"}}>{item.points}</p>
          </Table.Cell>
        </Table.Row>
      );
    });
  }

  render() {
    if (this.state.login) {
      return (
          <Layout accountType={this.state.accountType}>
            <Container style={{width: "100%", height:"100vh"}}>
              <Grid centered columns={2}>
                <Grid.Column>
                  <Divider hidden/>
                  <Header as='h2' textAlign='center'>
                      Questions Posted
                  </Header>
                  <Divider hidden/>
                  <Container>
                    <Table>
                      <Table.Header>
                        {this.renderQuestionHeader()}
                      </Table.Header>
                      <Table.Body>
                        {this.renderQuestionList()}
                      </Table.Body>
                    </Table>
                    <Divider hidden/>
                    <div style={{ marginTop: 20 }}>Found {this.props.summaries.length} Item(s).</div>
                  </Container>
                  {this.renderLeaderBoard()}
                  {this.renderTopTags()}
                </Grid.Column>
              </Grid>
            </Container>
          </Layout>
      );
    } else {
      return (
        <Layout accountType={this.state.accountType}>
          <Container style={{width: "100%", height:"100vh"}}>
            <Modal open={this.state.loading} basic size='small'>
              <Header icon='ethereum' content='Loading Resources' />
              <Modal.Content>
                <Loader active inline="centered">
                  Loading Deployed Questions
                </Loader>
              </Modal.Content>
            </Modal>
            <Modal open={!this.state.login && !this.state.loading} basic size='small'>
              <Header icon='ethereum' content='Not Registered' />
              <Modal.Content>
                <p>
                  You are currently not registered to access the platform. Please have your account registered.
                </p>
              </Modal.Content>
              <Modal.Actions>
                <Button color='green' inverted attached='bottom' onClick={() => {
                  Router.pushRoute('/')}
                }>
                  Register For Account
                </Button>
              </Modal.Actions>
            </Modal>
          </Container>
        </Layout>
      );
    }
  }
}

export default HomePage;
