import React, { Component } from 'react';
import Layout from '../components/Layout';
import { Container, Segment, Header, Divider, Button, Icon } from "semantic-ui-react";
import {
  BlobProvider,
  Document,
  Page,
  Text,
  View,
  Image,
  Link,
  StyleSheet,
  ReactPDF
} from "@react-pdf/renderer";

class AdminPage extends Component {
  constructor(props) {
    super(props);
    this.state = {
    };
  }

  export = () => {
  }

  render() {
    return (
      <Layout>
        <Container style={{height:"100vh"}}>
          <Divider hidden/>
          <Segment raised textAlign='left'>
            <Header as='h2' textAlign='center'>
              Exporting Questions & Answers As PDF
            </Header>
          </Segment>
          <Divider hidden/>
          <Button icon labelPosition='left' color="red" onClick={this.export}>
            <Icon name='download'/>
            Export As PDF
          </Button>
        </Container>
      </Layout>
    )
  }
}

export default AdminPage;
