import React, { Component } from 'react';
import Layout from '../components/Layout';
import { Container, Segment, Header, Divider, Button, Icon, Radio } from "semantic-ui-react";

class FeedbackPage extends Component {

  state = {
    like: "",
    likeGreen:"false",
    likeRed:"false",
    usefulness: "",
    easy: ""
  }

  onLike = (value) => {
    if (value)
      this.setState({ like: "Yes"});
  }

  render() {
    return(
      <Layout>
        <Container style={{height:"100vh"}}>
          <Divider hidden/>
          <Segment raised textAlign='left'>
            <Header as='h2' textAlign='center'>
              Collection of Feedback For System
            </Header>
          </Segment>
          <Divider hidden/>
          <Header as='h3' textAlign='left'>
            Do you like the system? Selected Value: <b>{this.state.like}</b>
          </Header>
          <Button icon labelPosition='left' color="green" compact={true} onClick={this.onLike(true)}>
            <Icon name='thumbs up outline' />
            Yes
          </Button>
          <Button icon labelPosition='left' color="red" active compact={true} onClick={this.onDislike}>
            <Icon name='thumbs down outline' />
            No
          </Button>
          <Divider />
        </Container>
      </Layout>
    );
  }
}

export default FeedbackPage;
