import React, { Component } from 'react';
import { Button, Checkbox, Form, Container, Divider, Input,
   Segment, Message, Icon, Modal, Header, Loader, Grid } from 'semantic-ui-react'
import LoginLayout from '../components/LoginLayout';
import web3 from '../ethereum/web3';
import credentials from '../ethereum/credentials';
import { Router } from '../routes';

class LoginPage extends Component {

  state = {
    emailAddress:'',
    loading: false,
    errorMessage:''
  };

  validate(emailAddress){
    const re = /\S+@e.ntu.edu.sg$/;
    return re.test(emailAddress);
  };

  onSubmit = async () => {
      this.setState({ loading: true, errorMessage: '' });
      // Check for valid NTU Student Email Address
      if (this.validate(this.state.emailAddress)){
        try {
            const accounts = await web3.eth.getAccounts();
            const _emailAddress = await credentials.methods.getBinding(accounts[0]).call();
            // Authenticate the Email Address
            if (this.state.emailAddress.localeCompare(_emailAddress) == 0){
              Router.pushRoute(`/home`); // Automatic redirect the user.
            }
            else {
              this.setState({ errorMessage: "Wrong Email Address" });
              this.setState({ loading: false });
            }
        } catch (err) {
            this.setState({ errorMessage: err.message });
        }
      } else {
        this.setState({ errorMessage: "Invalid Email Address" });
        this.setState({ loading: false });
      }
  };

  renderForm() {
    return(
      <Grid textAlign='center' style={{ height: '40%' }} verticalAlign='middle'>
        <Grid.Column style={{ maxWidth: 450 }}>
          <Header as='h2' textAlign='center'>
            <Icon name='ethereum' /> Log-in to your account
          </Header>
          <Form size='large' error={!!this.state.errorMessage}>
            <Segment stacked>
              <Form.Field>
                <label>NTU Student Email Address</label>
                <Input
                    icon='user'
                    iconPosition='left'
                    placeholder='example@e.ntu.edu.sg'
                    value={this.state.emailAddress}
                    onChange={event => this.setState({ emailAddress: event.target.value})}
                />
              </Form.Field>
              <Message error header="Oops!" content={this.state.errorMessage} />
              <Modal open={this.state.loading} trigger={<Button color='blue' fluid size='large' onClick={this.onSubmit}>Login</Button>} basic size='small'>
                <Header icon='ethereum' content='Verifying Your Email Address' />
                <Modal.Content>
                  <Loader active inline="centered">
                    Authenticating Credentials
                  </Loader>
                </Modal.Content>
              </Modal>
            </Segment>
          </Form>
          <Message>
            Not yet registered your NTU Student Email to your Ethereum Account?
            <Modal trigger={<a href="/register"> Register Now</a>} basic size='small'>
              <Header icon='ethereum' content='Please Hang On While We Redirect You' />
              <Modal.Content>
                <Loader active inline="centered">
                  Redirecting to Registration
                </Loader>
              </Modal.Content>
            </Modal>
          </Message>
        </Grid.Column>
    </Grid>
    );
  }

  render() {
    return(
      <LoginLayout>
          <Container style={{height:"100vh"}}>
              {this.renderForm()}
          </Container>
      </LoginLayout>
    );
  }
}

export default LoginPage;
