import React, { Component } from 'react';
import Layout from '../components/Layout';
import { Container, Segment, Header, Divider, Button, Icon } from "semantic-ui-react";

// Ensure jsPDF doesnt import on Server Side
let jsPDF = null;
if (typeof window !== "undefined") {
  import("jspdf").then(module => {
    jsPDF = module.default;
  });
}

class AdminPage extends Component {
  constructor(props) {
    super(props);
    this.state = {
    };
  }

  export = () => {
    var doc = new jsPDF()

    // I know the proper spelling is colour ;)
    doc.setTextColor(100)
    doc.text(20, 20, 'This is gray.')

    doc.setTextColor(150)
    doc.text(20, 30, 'This is light gray.')

    doc.setTextColor(255, 0, 0)
    doc.text(20, 40, 'This is red.')

    doc.setTextColor(0, 255, 0)
    doc.text(20, 50, 'This is green.')

    doc.setTextColor(0, 0, 255)
    doc.text(20, 60, 'This is blue.')

    doc.save('testing.pdf')
  }

  render() {
    return (
      <Layout>
        <Container style={{height:"100vh"}}>
          <Divider hidden/>
          <Segment raised textAlign='left'>
            <Header as='h2' textAlign='center'>
              Exporting Questions & Answers As PDF
            </Header>
          </Segment>
          <Divider hidden/>
          <Button icon labelPosition='left' color="red" onClick={this.export}>
            <Icon name='download'/>
            Export As PDF
          </Button>
        </Container>
      </Layout>
    )
  }
}

export default AdminPage;
