import React, { Component } from 'react';
import { Form, Button, Input, Message, Dropdown, Header, Segment, Container, TextArea } from 'semantic-ui-react';
import Layout from '../components/Layout';
import factory from '../ethereum/factory';
import web3 from '../ethereum/web3';
import { Router } from '../routes';

class NewPost extends Component {
    state = {
        questionTitle: '',
        content:'',
        reward:'',
        maxDuration:'',
        errorMessage: '',
        loading: false,
        userWallet: ''
    };

    static async getInitialProps () {
        const accounts = await web3.eth.getAccounts();
        return { accounts }
    }

    onSubmit = async event => {
        event.preventDefault(); // prevent default submission of the form to the backend (the browser does that automatically)

        this.setState({ loading: true, errorMessage: '' });

        try {
            const accounts = await web3.eth.getAccounts();
            // Create a new post
            await factory.methods
            .createQuestion(this.state.questionTitle, this.state.content, this.state.reward, this.state.maxDuration)
            .send({
                from: this.props.accounts[0]
            });

            Router.pushRoute('/'); // Automatic redirect the user.
        } catch (err) {
            this.setState({ errorMessage: err.message });
        }

        this.setState({ loading: false });
    };

    render() {
        return (
            <Layout>
                <Container>
                  <Segment textAlign='center' as='h3'>Creating Of New Question</Segment>
                  <Form onSubmit={this.onSubmit} error={!!this.state.errorMessage} style={{ marginTop: '10px' }}>
                      <Form.Field>
                          <label>Question Title</label>
                          <Input
                              placeholder="Enter Title"
                              value={this.state.questionTitle}
                              onChange={event => this.setState({ questionTitle: event.target.value })}
                          />
                      </Form.Field>
                      <Form.Field>
                          <label>Question Description</label>
                          <TextArea
                              placeholder="Enter Description"
                              value={this.state.content}
                              onChange={event => this.setState({ content: event.target.value })}
                          />
                      </Form.Field>
                      <Form.Field>
                          <label>Ether</label>
                          <Input
                              label="ether"
                              labelPosition="right"
                              value={this.state.reward}
                              onChange={event => this.setState({ reward: event.target.value })}
                          />
                      </Form.Field>
                      <Form.Field>
                          <label>Maximum Duration</label>
                          <Input
                              label="hour(s)"
                              labelPosition="right"
                              value={this.state.maxDuration}
                              onChange={event => this.setState({ maxDuration: event.target.value })}
                          />
                      </Form.Field>
                      <Message error header="Oops!" content={this.state.errorMessage} />
                      <Button loading={this.state.loading} primary>Create!</Button>
                  </Form>
                </Container>
            </Layout>
        );
    }
}

export default NewPost;
