import React, { Component } from 'react';
import { Button, Checkbox, Form, Container, Divider, Input, Segment, Message } from 'semantic-ui-react'
import Layout from '../components/Layout';
import web3 from '../ethereum/web3';
import credentials from '../ethereum/credential';
import { Router } from '../routes';

class LoginPage extends Component {

  state = {
    emailAddress:'',
    loading: false,
    errorMessage:''
  };

  validate(emailAddress){
    var re = /\S+@e.ntu.edu.sg/;
    return re.test(emailAddress);
  };

  onSubmit = async () => {
      this.setState({ loading: true, errorMessage: '' });
      // Check for valid NTU Student Email Address
      if (this.validate(this.state.emailAddress)){
        try {
            const accounts = await web3.eth.getAccounts();
            // Create a new record
            await credentials.methods
            .createBinding(this.state.emailAddress)
            .send({
                from: accounts[0]
            });
            Router.pushRoute('/'); // Automatic redirect the user.
        } catch (err) {
            this.setState({ errorMessage: err.message });
        }
        this.setState({ loading: false });
      } else {
        this.setState({ errorMessage: "Invalid Email Address" });
        this.setState({ loading: false });
      }
  };

  renderForm() {
    return(
      <Form error={!!this.state.errorMessage}>
        <Segment raised textAlign={"center"}>
          Please Login Using Your Registered NTU Email Address
        </Segment>
        <Form.Field>
          <label>NTU Student Email Address</label>
          <Input
              placeholder='example@e.ntu.edu.sg'
              value={this.state.emailAddress}
              onChange={event => this.setState({ emailAddress: event.target.value})}
          />
        </Form.Field>
        <Message error header="Oops!" content={this.state.errorMessage} />
        <Button loading={this.state.loading} primary onClick={this.onSubmit}>Log In</Button>
      </Form>
    );
  }

  render() {
    return(
      <Layout>
        <Divider hidden/>
        <Container>

          {this.renderForm()}
        </Container>
      </Layout>
    );
  }
}

export default LoginPage;
