import React, { Component } from 'react';
import { Button, Checkbox, Form, Container, Divider, Input } from 'semantic-ui-react'
import Layout from '../components/Layout';

class LoginPage extends Component {

  renderForm() {
    return(
      <Form>
        <Form.Field>
          <label>Ethereum Wallet Address</label>
          <Input placeholder='0x573391b457fF7E94622b4CFa6a49Cd9e5ef57346' />
        </Form.Field>
        <Form.Field>
          <label>NTU Student Email Address</label>
          <input placeholder='example@e.ntu.edu.sg' />
        </Form.Field>
        <Button type='submit'>Submit</Button>
      </Form>
    );
  }

  render() {
    return(
      <Layout>
        <Divider hidden/>
        <Container>
          {this.renderForm()}
        </Container>
      </Layout>
    );
  }
}

export default LoginPage;
