import React, { Component } from 'react';
import { Table, Popup } from 'semantic-ui-react';
import { Link } from '../routes';
import moment from 'moment';

class QuestionRow extends Component {
    render() {
        const { summary } = this.props;

        var currentTime = (new Date).getTime();
        var maxDuration = (parseFloat(summary[3]) / 60 / 60).toFixed(2).toString();
        var publishTime = moment.unix(summary[6][0]).format('dddd, Do MMMM YYYY, h:mm:ss a');
        var publishTimeMs = moment.unix(summary[6][0]).valueOf();
        var maxDurationMs = moment.unix(summary[3]).valueOf();

        let remainingTime = publishTimeMs + maxDurationMs - currentTime;
        if (remainingTime < 0) {
            remainingTime = 0;
        }

        return (
          <React.Fragment>
              <Table definition>
                  <Table.Body>
                    <Table.Row>
                        <Table.Cell width={2}>Question Title</Table.Cell>
                        <Table.Cell style={{fontSize: '20px', lineHeight: '1.5'}}>
                            {summary[0]}
                        </Table.Cell>
                    </Table.Row>
                      <Table.Row>
                          <Table.Cell width={2}>Description</Table.Cell>
                          <Table.Cell style={{fontSize: '20px', lineHeight: '1.5'}}>
                              {summary[1]}
                          </Table.Cell>
                      </Table.Row>
                      <Table.Row>
                          <Table.Cell>Reward (ETH)</Table.Cell>
                          <Table.Cell style={{fontSize: '20px'}}>{summary[2]}</Table.Cell>
                      </Table.Row>
                      <Table.Row>
                          <Table.Cell>Publish Time</Table.Cell>
                          <Table.Cell style={{fontSize: '20px'}}>{publishTime}</Table.Cell>
                      </Table.Row>
                      <Table.Row>
                          <Table.Cell>Duration (hours)</Table.Cell>
                          <Table.Cell style={{fontSize: '20px'}}>
                              <span style={{verticalAlign: 'middle', lineHeight: '33px'}}>
                                  {maxDuration}
                              </span>
                              {remainingTime > 0
                                  ? <Popup
                                      trigger={
                                          <Button style={{float: 'right', verticalAlign: 'middle'}}
                                                  icon='clock'
                                                  color="violet"
                                          />}
                                      content={
                                          <span>
                                          <span>Remaining time - </span>
                                          <Countdown date={Date.now() + remainingTime}>
                                          </Countdown>
                                      </span>}
                                      position='bottom right'
                                      inverted />
                                  : <Popup
                                      trigger={
                                          <Button style={{float: 'right', verticalAlign: 'middle'}}
                                                  icon='clock'
                                                  color="red"
                                          />
                                      }
                                      content={"EXPIRED"}
                                      position='bottom right'
                                      inverted />
                              }
                          </Table.Cell>
                      </Table.Row>
                  </Table.Body>
              </Table>
          </React.Fragment>
        );
    }
}

export default QuestionRow;
