import React, { Component } from 'react';
import { Button, Header, Table, Statistic, Rating, Grid, Message, Divider, Container, Icon } from 'semantic-ui-react';
import factory from '../ethereum/factory';
import Question from '../ethereum/question';
import Layout from '../components/Layout';
import {Link, Router} from '../routes';
import web3 from '../ethereum/web3';

class HomePage extends Component {

    static async getInitialProps () {
        const accounts = await web3.eth.getAccounts();
        // Retrieve an array of all our deployed questions addresses.
        const deployedQuestions = await factory.methods.getDeployedQuestions().call();
        const deployedQuestionsCount = deployedQuestions.length;

        // Stores all Question contracts
        let questions = [];
        let summaries = [];
        let answersList = [];
        for(let i=0; i<deployedQuestionsCount; i++) {
          questions[i] = Question(deployedQuestions[i]);
          summaries[i] = await questions[i].methods.getSummary().call();
          summaries[i][6] = await questions[i].methods.start().call();
          answersList[i] = await questions[i].methods.getAnswerList().call();
        }
        console.log("Init")

        return { summaries, deployedQuestions, accounts, answersList };
    }

    renderQuestionList() {
        const questionsDeployed = this.props.summaries;
        const answers = this.props.answersList;

        return questionsDeployed.map((summary, index) => {
            const state = summary[4];
            console.log(state)
            return <Table.Row key={index}>
              <Table.Cell textAlign='center' width={2}>
                  {
                    state == 0 ? <Statistic size='mini' color='red'>
                                    <Statistic.Value><span
                                        style={{fontSize: 15, color: '#6A737C'}}> Answering
                                                    </span></Statistic.Value>
                                    <Statistic.Label><span style={{fontSize: 15, color: '#6A737C'}}>Phase</span></Statistic.Label>
                                </Statistic>
                              : <Statistic size='mini' color='red'>
                                    <Statistic.Value><span
                                        style={{fontSize: 15, color: '#6A737C'}}> Rating
                                                    </span></Statistic.Value>
                                    <Statistic.Label><span style={{fontSize: 15, color: '#6A737C'}}>Phase</span></Statistic.Label>
                                </Statistic>
                  }
                </Table.Cell>
                <Table.Cell textAlign='center' width={2}>
                    <Statistic size='mini' color='red'>
                        <Statistic.Value><span
                            style={{fontSize: 15, color: '#6A737C'}}><Icon name='thumbs up' /> 1
                                        </span></Statistic.Value>
                        <Statistic.Label><span style={{fontSize: 15, color: '#6A737C'}}>Up Votes</span></Statistic.Label>
                    </Statistic>
                </Table.Cell>
                <Table.Cell textAlign='center' width={2}>
                    <Statistic size='mini' color='red'>
                        <Statistic.Value><span
                            style={{fontSize: 15, color: '#6A737C'}}><Icon name='thumbs down' /> 1
                                        </span></Statistic.Value>
                        <Statistic.Label><span style={{fontSize: 15, color: '#6A737C'}}>Down Votes</span></Statistic.Label>
                    </Statistic>
                </Table.Cell>
                <Table.Cell textAlign='center' width={2}>
                    <Statistic size='mini' color='red'>
                        <Statistic.Value><span style={{fontSize: 15, color: '#6A737C'}}>{answers[index].length}</span></Statistic.Value>
                        <Statistic.Label><span style={{fontSize: 15, color: '#6A737C'}}>answers</span></Statistic.Label>
                    </Statistic>
                </Table.Cell>
                <Table.Cell textAlign='center' width={2}>
                    <Statistic size='mini' color='red'>
                        <Statistic.Value><span style={{fontSize: 15, color: '#6A737C'}}>{summary[2]}</span></Statistic.Value>
                        <Statistic.Label><span style={{fontSize: 15, color: '#6A737C'}}>ethers</span></Statistic.Label>
                    </Statistic>
                </Table.Cell>
                <Table.Cell textAlign='left'>
                    <Grid.Row textAlign='left'>
                        <span style={{fontSize: 18, color: '#6A737C', cursor: 'pointer'}} onClick={() => Router.pushRoute(`/questions/${this.props.deployedQuestions[index]}`)}><a>{summary[0]}</a></span></Grid.Row>
                </Table.Cell>
            </Table.Row>
      });
    }

    render() {
        return (
            <Layout>
                <div>
                    <Header as='h2' textAlign='center'>
                        Questions Posted
                    </Header>
                </div>
                <Divider hidden/>
                <Container>
                  <Table>
                    <Table.Body>
                      {this.renderQuestionList()}
                    </Table.Body>
                  </Table>
                </Container>
                <Divider hidden/>
            </Layout>
        );
    }
}

export default HomePage;
