import React, { Component } from 'react';
import { Button, Card, Segment, Container, List, Transition, Popup, Modal,
         Header, Loader, Icon, Divider } from "semantic-ui-react";
import AccountIssueModal from '../components/AccountIssueModal';
import web3 from '../ethereum/web3'
import Layout from '../components/Layout';
import factory from '../ethereum/factory';
import credentials from '../ethereum/credentials';
import EthQuestionToken from '../ethereum/token';
import Profile from '../ethereum/profile';
import { checkLogin } from '../utils/functions'

class userProfile extends Component {
  constructor(props) {
    super(props);
    this.state = {
      visible: false,
      login: false,
      loading: true,
      accountType: null,
      account: null,
      token: null,
      emailAddress: null,
      points: null,
      transactions: []
    };
  }

  toggleVisibility = () =>
    this.setState((prevState) => ({ visible: !prevState.visible }))

  async componentDidMount() {
    let login = false;
    // Retrieve the Ethereum Account from MetaMask
    const accounts = await web3.eth.getAccounts();
    if (await checkLogin(accounts[0])) {
      login = true;
      // Get profile data
      const profileAddress = await factory.methods.getProfile(accounts[0]).call();
      const profile = Profile(profileAddress);
      const emailAddress = await credentials.methods.getBinding(accounts[0]).call();
      const token = await EthQuestionToken.methods.balanceOf(accounts[0]).call()*1e-4.toFixed(4);
      const points = await profile.methods.getPoints().call();
      const transactions = await profile.methods.getTransactions().call();

      // Get Account Type
      let accountType = await profile.methods.getAccountType().call();
      accountType == 0 ? accountType = "Admin" : accountType = "User"
      this.setState( { accountType: accountType,
                       account: accounts[0],
                       token: token,
                       emailAddress: emailAddress,
                       points: points,
                       transactions: transactions,
                       login: true });
    }
    this.setState( { loading: false });
  }

  renderTransactions() {
    const transactions = this.state.transactions;
    return transactions.map((transaction, index) => {
      return(
        <List.Item key={index}>
          <List.Icon name="calendar outline" size='large' verticalAlign='middle'/>
          <List.Content>
            {transaction}
          </List.Content>
        </List.Item>
      );
    })
  }

  render() {
    if (this.state.login) {
      return(
        <Layout accountType={this.state.accountType}>
          <Divider hidden/>
          <Container>
            <Card color = "red" centered fluid>
              <Card.Content>
                <Header as='h1' textAlign='center'>
                  <Icon name='user' circular/>
                </Header>
                <Card.Header textAlign='center'>
                  Account Details:
                  <div style={{color: "red", fontSize: "small", wordWrap: "break-word" }}>{this.state.account}</div>
                </Card.Header>
                <Card.Meta textAlign='center'>
                  <b>{this.state.accountType}</b>
                </Card.Meta>
                <Card.Description textAlign='center'>
                  <b>EQT Balance:</b> {this.state.token}
                </Card.Description>
                <Card.Description textAlign='center'>
                  <b>Points Accumulated:</b> {this.state.points}
                </Card.Description>
                <Card.Description textAlign='center'>
                  <b>Valid Email Address:</b> {this.state.emailAddress}
                </Card.Description>
              </Card.Content>
            </Card>
          </Container>
          <Divider hidden/>
          <Container>
            <Segment textAlign="center">
              <b style={{fontSize:"20px"}}>Transaction History</b>
              <Popup
                trigger={
                  <Button
                    icon={this.state.visible ? "minus" : "plus"}
                    onClick={this.toggleVisibility}
                    floated="right"
                    size="mini"
                  />
                }
                content={"Click to expand/close"}
                inverted/>
            </Segment>
            <Transition visible={this.state.visible} animation='scale' duration={500}>
              <List divided size="huge">
                {this.renderTransactions()}
              </List>
            </Transition>
          </Container>
        </Layout>
      );
    } else {
      return (
        <Layout accountType={this.state.accountType}>
          <AccountIssueModal loading={this.state.loading} login={this.state.login} />
        </Layout>
      );
    }
  }
}

export default userProfile;
