import React, { Component } from 'react';
import Layout from '../components/Layout';
import { Container, Segment, Header, Divider, Button, Icon, Dropdown } from "semantic-ui-react";
import web3 from '../ethereum/web3';
import factory from '../ethereum/factory';
import credentials from '../ethereum/credentials';

// Ensure jsPDF does not import on Server Side
let jsPDF = null;
if (typeof window !== "undefined") {
  import("jspdf").then(module => {
    jsPDF = module.default;
  });
}

class AdminPage extends Component {
  constructor(props) {
    super(props);
    this.state = {
      questionOptions: [],
      questionNumber: ''
    };
  }

  static async getInitialProps () {
      const accounts = await web3.eth.getAccounts();
      // Redirect E-Wallet that are not Logged In to the Login Page
      if (await credentials.methods.getBinding(accounts[0]).call() === ""){
          Router.pushRoute(`/`);
      }

      // Retrieve an array of addresses of questions deployed
      let deployedQuestions = await factory.methods.getDeployedQuestions().call();
      let deployedQuestionsCount = deployedQuestions.length;

      return { accounts, deployedQuestionsCount }
  }

  async componentWillMount() {
    // Retrieve the Ethereum Account from MetaMask
    const accounts = await web3.eth.getAccounts();
    // Redirect E-Wallet that are not Logged In to the Login Page
    if (await credentials.methods.getBinding(accounts[0]).call() === ""){
        Router.pushRoute(`/`); // Automatic redirect the user to Login or Sign Up
    }
    let questionOptions = [];
    for (let i = 0; i < this.props.deployedQuestionsCount; i++) {
        questionOptions.push({
            key: i+1,
            text: i+1,
            value: i+1
        });
    }
    this.setState( { questionOptions: questionOptions });
  }

  export = async () => {
    // Check that a question must be selected
    if (this.state.questionNumber != "") {
      let questionNumber = Number(this.state.questionNumber) - 1;
      let doc = new jsPDF()
      let question = this.props.deployedQuestions[questionNumber]
      console.log(questionNumber);
    }
  }

  render() {
    return (
      <Layout>
        <Container style={{height:"100vh"}}>
          <Divider hidden/>
          <Segment raised textAlign='left'>
            <Header as='h2' textAlign='center'>
              Exporting Questions & Answers As PDF
            </Header>
          </Segment>
          <Divider hidden/>
          <p style={{fontSize:'16px'}}>
            Select the question to export the question and it's corresponding answers
          </p>
          <Dropdown clearable
                    options={this.state.questionOptions}
                    selection
                    placeholder='Select the question to export'
                    value={this.state.questionNumber}
                    onChange={ (e, { value }) => this.setState({ questionNumber: value })}/>
          <Button icon labelPosition='left' color="red" onClick={this.export} style={{left:"20px"}}>
            <Icon name='download'/>
            Export As PDF
          </Button>
        </Container>
      </Layout>
    )
  }
}

export default AdminPage;
