import React, { Component } from 'react';
import { Icon, Segment, Divider, Container } from 'semantic-ui-react';
import { Link, Router } from '../routes';

class LoginHeader extends Component {
  render() {
    return(
      <Container style={{width:"100%"}}>
          <Segment style={{marginBottom:'0px', fontSize:'20px', borderRadius:'0px', backgroundColor:'#EDC7B7', border:'0px'}} as='h1' textAlign='center'>
            <Icon name='ethereum' />Block-Chain Based Question Answering System
          </Segment>
          <Divider hidden/>
      </Container>
    );
  }
}

export default LoginHeader;
