import React, { Component } from 'react';
import web3 from '../ethereum/web3'
import Layout from '../components/Layout';
import factory from '../ethereum/factory';
import Profile from '../ethereum/profile';
import {Button, Card, Icon, Image} from "semantic-ui-react";

class userProfile extends Component {

  static async getInitialProps () {
      const account = await web3.eth.getAccounts();
      const profileAddress = await factory.methods.getProfile(account[0]).call();
      const profile = Profile(profileAddress);
      const token = profile.methods.getToken().call();
      return { account, token };
  }

  render() {
    return(
      <Layout>
          <React.Fragment>
              <Card color = "red" centered>

                <Card.Content>
                  <Card.Header>
                    Account:
                    <div style={{color: "red", fontSize: "small", wordWrap: "break-word" }}>{this.props.account[0]}</div>
                  </Card.Header>

                  <Card.Meta>
                    User
                  </Card.Meta>

                  <Card.Description>
                    <b>Token Available:</b> {this.props.token}
                  </Card.Description>
                </Card.Content>
              </Card>
          </React.Fragment>
      </Layout>
    );
  }
}

export default userProfile
