    doc.text('Hello world!', 10, 10)

    fs.writeFileSync('./output.pdf', doc.output())
    // doc.save('a4.pdf')
  }

  render() {
    return (
      <Layout>
        <Container style={{height:"100vh"}}>
          <Divider hidden/>
          <Segment raised textAlign='left'>
            <Header as='h2' textAlign='center'>
              Exporting Questions & Answers As PDF
            </Header>
          </Segment>
          <Divider hidden/>
          <Button icon labelPosition='left' color="red" onClick={this.export}>
            <Icon name='download'/>
            Export As PDF
          </Button>
        </Container>
      </Layout>
    )
  }
}

export default AdminPage;
