import React, { Component } from 'react';
import { Form, Button, Input, Message, Dropdown, Header, Segment, Container } from 'semantic-ui-react';
import Layout from '../components/Layout';
import factory from '../ethereum/factory';
import web3 from '../ethereum/web3';
import { Router } from '../routes';

class NewPost extends Component {
    state = {
        postContent: '',
        errorMessage: '',
        loading: false,
        userWallet: ''
    };

    static async getInitialProps () {
        const accounts = await web3.eth.getAccounts();
        return { accounts }
    }

    onSubmit = async event => {
        event.preventDefault(); // prevent default submission of the form to the backend (the browser does that automatically)

        this.setState({ loading: true, errorMessage: '' });

        try {
            const accounts = await web3.eth.getAccounts();
            // Create a new post
            await factory.methods
            .createQuestion(this.state.postContent, d.getDate(), month, d.getFullYear())
            .send({
                from: this.props.accounts[0]
            });

            Router.pushRoute('/'); // Automatic redirect the user.
        } catch (err) {
            this.setState({ errorMessage: err.message });
        }

        this.setState({ loading: false });
    };

    render() {
        return (
            <Layout>
                <Container>
                  <Segment textAlign='center' as='h3'>Creating Of New Question</Segment>
                    <Form error={!!this.state.errorMessage} success={!!this.state.successMessage}>
                        <Form.Field>
                            <label>Category</label>
                            <Dropdown placeholder='Choose one ...'
                                      openOnFocus
                                      selection
                                      options={tagOptions}
                                      onChange={event => this.setState({ category: event.target.textContent })}>
                            </Dropdown>
                        </Form.Field>
                        <Form.Field>
                            <label>Title</label>
                            <Input
                                placeholder="Enter Title"
                                value={this.state.questionTitle}
                                onChange={event => this.setState({ questionTitle: event.target.value })}
                            />
                        </Form.Field>
                        <Form.Field>
                            <label>Question Description</label>
                            <TextArea
                                placeholder="Enter Description"
                                value={this.state.description}
                                onChange={event => this.setState({ description: event.target.value })}
                            />
                        </Form.Field>
                        <Form.Field>
                            <label>Ether</label>
                            <Input
                                label="ether"
                                labelPosition="right"
                                value={this.state.deposit}
                                onChange={event => this.setState({ deposit: event.target.value })}
                            />
                        </Form.Field>
                        <Form.Field>
                            <label>Maximum Duration</label>
                            <Input
                                label="hour(s)"
                                labelPosition="right"
                                value={this.state.maxDuration}
                                onChange={event => this.setState({ maxDuration: event.target.value })}
                            />
                        </Form.Field>
                        <Form.Field>
                            {this.renderFiles(elmFiles)}
                        </Form.Field>
                        <Button loading={this.state.loading} primary disabled={this.state.disabled} onClick={() => this.setState({ popup: true })}>Post Your Question</Button>
                        <Message error header="Oops! Something went wrong" content={this.state.errorMessage} />
                        <Message success header="Success!" content={this.state.successMessage} />
                    </Form>
                </Container>
            </Layout>
        );
    }
}

export default NewPost;
