import React, { Component } from 'react';
import web3 from '../ethereum/web3'
import Layout from '../components/Layout';
import factory from '../ethereum/factory';
import credentials from '../ethereum/credentials';
import Profile from '../ethereum/profile';
import { Router } from '../routes';
import {Button, Card, Segment, Container, List} from "semantic-ui-react";

class userProfile extends Component {

  static async getInitialProps () {
      const accounts = await web3.eth.getAccounts();
      const account = accounts[0];
      if (await credentials.methods.getBinding(account).call() === ""){
          Router.pushRoute(`/`); // Automatic redirect the user to Login or Sign Up
      }
      const profileAddress = await factory.methods.getProfile(account).call();
      const profile = Profile(profileAddress);
      const emailAddress = await credentials.methods.getBinding(account).call();
      const token = await profile.methods.getToken().call();
      const points = await profile.methods.getPoints().call();
      const transactions = await profile.methods.getTransactions().call();
      return { account, token, emailAddress, points, transactions };
  }

  // Redirect E-Wallet that are not Logged In to the Login Page
  async componentWillMount() {
    // Retrieve the Ethereum Account from MetaMask
    const accounts = await web3.eth.getAccounts();
    if (await credentials.methods.getBinding(accounts[0]).call() === ""){
        Router.pushRoute(`/`); // Automatic redirect the user to Login or Sign Up
    }
  }

  renderTransactions() {
    const transactions = this.props.transactions;
    return transactions.map((transaction, index) => {
      return(
        <List.Item>
          <List.Icon name="calendar outline" size='large' verticalAlign='middle'/>
          <List.Content>
            {transaction}
          </List.Content>
        </List.Item>
      );
    })
  }

  render() {
    return(
      <Layout>
        <Card color = "red" centered style={{width:"20%"}}>
          <Card.Content>
            <Card.Header>
              Account Details:
              <div style={{color: "red", fontSize: "small", wordWrap: "break-word" }}>{this.props.account}</div>
            </Card.Header>
            <Card.Meta>
              User
            </Card.Meta>
            <Card.Description>
              <b>Token Available:</b> {this.props.token}
            </Card.Description>
            <Card.Description>
              <b>Points Accumulated:</b> {this.props.points}
            </Card.Description>
            <Card.Description>
              <b>Valid Email Address:</b> {this.props.emailAddress}
            </Card.Description>
          </Card.Content>
        </Card>
        <Container style={{width:"80%", height:"100vh"}}>
          <Segment textAlign={"center"}>
            <b>Transaction History</b>
          </Segment>
          <List divided relax>
            {this.renderTransactions()}
          </List>
        </Container>
      </Layout>
    );
  }
}

export default userProfile;
