import React, { Component } from 'react';
import { Button, Checkbox, Form, Container, Divider, Input, Segment } from 'semantic-ui-react'
import Layout from '../components/Layout';

class LoginPage extends Component {

  state = {
    emailAddress:'',
    loading: false
  };

  onSubmit = async () => {
      this.setState({ loading: true, errorMessage: '' });
      try {
          const accounts = await web3.eth.getAccounts();
          // Create a new record
          await factory.methods
          .createQuestion(this.state.questionTitle, this.state.content, Number(this.state.reward), parseFloat(this.state.maxDuration) * 60 * 60,
            this.state.fileHashes_array, this.state.fileNames_array)
          .send({
              from: accounts[0]
          });
          Router.pushRoute('/'); // Automatic redirect the user.
      } catch (err) {
          this.setState({ errorMessage: err.message });
      }
      this.setState({ loading: false });
  };

  renderForm() {
    return(
      <Form>
        <Segment raised></Segment>
        <Form.Field>
          <label>NTU Student Email Address</label>
          <Input
              placeholder='example@e.ntu.edu.sg'
              value={this.state.emailAddress}
              onChange={event => this.setState({ emailAddress: event.target.value})}
          />
        </Form.Field>
        <Button loading={this.state.loading} primary onClick={this.onSubmit}>Submit</Button>
      </Form>
    );
  }

  render() {
    return(
      <Layout>
        <Divider hidden/>
        <Container>

          {this.renderForm()}
        </Container>
      </Layout>
    );
  }
}

export default LoginPage;
