import React, { Component } from 'react';
import { Table, Popup, Button, Container, Form, Header, Comment,
  Segment, Message, Divider, Icon, Label, Modal, Loader, Image, Grid } from 'semantic-ui-react';
import { Link, Router } from '../routes';
import moment from 'moment';
import web3 from '../ethereum/web3';
import Question from '../ethereum/question';
import EthQuestionToken from '../ethereum/token';
import credentials from '../ethereum/credentials';
import Profile from '../ethereum/profile'
import factory from '../ethereum/factory';
import Layout from '../components/Layout';
import Countdown from 'react-countdown-now';
import { Context, Text } from 'react-mathjax2';
import Footer from '../components/Footer';
import { getIpfsHash } from './ipfs';
import { logging } from "./logger";

class QuestionRow extends Component {
  constructor(props) {
    super(props);
    this.state = {
      profileOwnerAddress: '',
      questionAddress: '',
      questionState: '',
      votingTime:'',
      voting: false,
      answer:'',
      errorMessage: '',
      loading: false,
      voteError: false,
      ownerError: false,
      voteMultipleError: false,
      stateChange: false,
      timeExtension: false,
      changeReward: false,
      fileLoading: false,
      buffer: null,
      files_array: [],
      fileHashes_array: [],
      fileNames_array: [],
      login: false,
      resourceLoading: true,
      accountType: null
    };
  }

  static async getInitialProps(props) {
    // Retrieve question data
    const questionAddress = props.query.value;
    const question = Question(questionAddress);
    const summary = await question.methods.getSummary().call();
    const time = await question.methods.getTime().call();
    const profileOwnerAddress = await factory.methods.getProfile(summary[7]).call();
    const answerList = await question.methods.getAnswerList().call();
    const iconList = ['alligator', 'anteater', 'armadillo', 'auroch', 'axolotl',
        'badger', 'bat', 'beaver', 'buffalo', 'camel', 'capybara',
        'chameleon', 'cheetah', 'chinchilla', 'chipmunk', 'chupacabra',
        'cormorant', 'coyote', 'crow', 'dingo', 'dinosaur', 'dolphin',
        'duck', 'elephant', 'ferret', 'fox', 'frog', 'giraffe', 'gopher',
        'grizzly', 'hedgehog', 'hippo', 'hyena', 'ibex', 'ifrit', 'iguana',
        'jackal', 'kangaroo', 'koala', 'kraken', 'lemur', 'leopard',
        'liger', 'llama', 'manatee', 'mink', 'monkey', 'moose', 'narwhal',
        'orangutan', 'otter', 'panda', 'penguin', 'platypus',
        'pumpkin', 'python', 'quagga', 'rabbit', 'raccoon', 'rhino',
        'sheep', 'shrew', 'skunk', 'squirrel', 'tiger', 'turtle', 'walrus',
        'wolf', 'wolverine', 'wombat'];
    const avatarListIcon = iconList.map((item)=>{
        return 'https://ssl.gstatic.com/docs/common/profile/' + item + '_lg.png';
    });
    return { summary, time, questionAddress, answerList, avatarListIcon, profileOwnerAddress };
  };

  async componentDidMount() {
    // Retrieve the Ethereum Account from MetaMask
    const accounts = await web3.eth.getAccounts();
    let login = false;
    if (await credentials.methods.getBinding(accounts[0]).call() === "") {
    } else {
      login = true;
      // Get Account Type
      let profileAddress = await factory.methods.getProfile(accounts[0]).call();
      let profile = Profile(profileAddress);
      let accountType = await profile.methods.getAccountType().call();
      accountType == 0 ? accountType = "Admin" : accountType = "User"
      this.setState({ login: login,
                      accountType: accountType });
    }
    this.setState({
      profileOwnerAddress: this.props.profileOwnerAddress,
      questionAddress: this.props.questionAddress,
      questionState: this.props.summary[4],
      votingTime: this.props.time[3],
      resourceLoading: false
    });
  }

  votePhase = async () => {
    // When in rewarded phase, all votings will be free
    try {
      if(this.state.questionState == 0) {
        const accounts = await web3.eth.getAccounts();
        const question = Question(this.state.questionAddress);
        // Ensure that only Creator of Question can access this function
        if (accounts[0] == await question.methods.getCreator().call()){
          this.setState({ stateChange: true});
          let _questionState = await question.methods.getQuestionState(EthQuestionToken._address).send(
            { from: accounts[0]}
          );
          // Set voting time inside smart contract as +1hr of now end time
          let _votingTime = await question.methods.getTime().call();
          this.setState({questionState: _questionState, votingTime: _votingTime[3] });
          Router.pushRoute(`/questions/${this.state.questionAddress}`)
        } else {
          this.setState({ ownerError: true});
        }
      }
    } catch (err) {
        console.log(err)
        this.setState({ errorMessage: err });
    }
    this.setState({ stateChange: false});
  }

  rewardPhase = async () => {
    try {
      if(this.state.questionState == 1) {
        const accounts = await web3.eth.getAccounts();
        const question = Question(this.state.questionAddress);
        // Ensure that only Creator of Question can access this function
        if (accounts[0] == await question.methods.getCreator().call()){
          this.setState({ stateChange: true});
          let logTransaction = logging("Awarded " + this.props.summary[2]*1e-4 + " EQT(s) as reward for Question Titled: " + this.props.summary[0]);
          let rewardMessage = logging("Rewarded " + this.props.summary[2]*1e-4 + " EQT(s) for most up votes answer");
          let voteMessage = logging("Rewarded 2 EQT(s) for voting the most up voted answer");
          let refundMessage = logging("Refunded " + this.props.summary[2]*1e-4 + " EQT(s) as there are no answers or answers with no votes");
          await factory.methods
          .shareTokenAt(this.state.questionAddress, logTransaction, rewardMessage, voteMessage, refundMessage, EthQuestionToken._address)
          .send({
              from: accounts[0]
          });
          // Change question state to rewarded
          let _questionState = await question.methods.getQuestionState(EthQuestionToken._address).send(
            { from: accounts[0]}
          );
          this.setState({questionState: _questionState });
          Router.pushRoute(`/questions/${this.state.questionAddress}`)
        } else {
          this.setState({ ownerError: true});
        }
      }
    } catch (err) {
      this.setState({ errorMessage: err.message });
    }
    this.setState({ stateChange: false});
  };

  timeExtension = async () => {
    try {
      const accounts = await web3.eth.getAccounts();
      const question = Question(this.state.questionAddress);
      // Ensure that only Creator of Question can access this function
      if (accounts[0] == await question.methods.getCreator().call()){
        this.setState({ timeExtension: true});
        await question.methods.timeExtension().send({
          from: accounts[0]
        });
        Router.pushRoute(`/questions/${this.state.questionAddress}`)
      } else {
        this.setState({ ownerError: true});
      }
    } catch (err) {
      this.setState({ errorMessage: err.message });
    }
    this.setState({ timeExtension: false})
  }

  increaseReward = async () => {
    try {
      const accounts = await web3.eth.getAccounts();
      const question = Question(this.state.questionAddress);
      // Ensure that only Creator of Question can access this function
      if (accounts[0] == await question.methods.getCreator().call()){
        this.setState({ changeReward: true});
        let logTransaction = logging("Increase Reward by 1 EQT(s) for Question Titled: " + this.props.summary[0]);
        await factory.methods.increaseReward(this.state.questionAddress, logTransaction, EthQuestionToken._address).send({
          from: accounts[0]
        });
        await EthQuestionToken.methods.transfer(this.state.questionAddress, 10e3).send({
          from: accounts[0]
        })
        Router.pushRoute(`/questions/${this.state.questionAddress}`)
      } else {
        this.setState({ ownerError: true});
      }
    } catch (err) {
      this.setState({ errorMessage: err.message });
    }
    this.setState({ changeReward: false})
  }

  onSubmitAnswer = async () => {
    this.setState({ loading: true, errorMessage: '' });
    try {
        const accounts = await web3.eth.getAccounts();
        const question = Question(this.state.questionAddress);
        // Ensure that creator of question cannot provide answers
        if (accounts[0] != await question.methods.getCreator().call()){
          // Logs the transactions
          let logTransaction = logging("Submitted Answer for Question Titled: " + this.props.summary[0]);

          // Create new answer
          await factory.methods
          .createAnswer(this.state.questionAddress, this.state.answer, this.state.fileHashes_array, this.state.fileNames_array, logTransaction)
          .send({
              from: accounts[0]
          });
          Router.pushRoute(`/questions/${this.state.questionAddress}`)
        } else {
          this.setState({ loading: false, errorMessage: "You cannot provide answer for your own question"})
        }
    } catch (err) {
        this.setState({ errorMessage: err.message });
    }
    this.setState({ loading: false, answer:'' });
  };

  upVote = async(index) => {
    this.setState({ voting: true })
    const accounts = await web3.eth.getAccounts();
    const question = await Question(this.state.questionAddress);
    const profile = await factory.methods.getProfile(accounts[0]).call();
    // Ensure that voter is not the answerer
    if (accounts[0] != await question.methods.getAnswerer(index).call()){
      // Cannot vote twice
      if (! await question.methods.checkVoter(index, profile).call()) {
        let logTransaction = logging("Up Voted An Answer for Question Titled: " + this.props.summary[0]);
        await factory.methods
        .upVote(this.state.questionAddress, index, logTransaction, EthQuestionToken._address)
        .send({
            from: accounts[0]
        });
        await EthQuestionToken.methods.transfer(await EthQuestionToken.methods.getOwner().call(), 10e3)
        .send({
            from: accounts[0]
        })
        Router.pushRoute(`/questions/${this.state.questionAddress}`)
      } else {
        this.setState({ voteMultipleError: true});
      }
    } else {
      this.setState({ voteError: true});
    }
    this.setState({ voting: false })
  };

  downVote = async(index) => {
    this.setState({ voting: true })
    const accounts = await web3.eth.getAccounts();
    const question = await Question(this.state.questionAddress);
    const profile = await factory.methods.getProfile(accounts[0]).call();
    // Ensure that voter is not the answerer
    if (accounts[0] != await question.methods.getAnswerer(index).call()){
      // Cannot vote twice
      if (! await question.methods.checkVoter(index, profile).call()) {
        let logTransaction = logging("Down Voted An Answer for Question Titled: " + this.props.summary[0]);
        await factory.methods
        .downVote(this.state.questionAddress, index, logTransaction, EthQuestionToken._address)
        .send({
            from: accounts[0]
        });
        await EthQuestionToken.methods.transfer(EthQuestionToken.methods.getOwner().call(), 10e3)
        .send({
            from: accounts[0]
        })
        Router.pushRoute(`/questions/${this.state.questionAddress}`)
      } else {
        this.setState({ voteMultipleError: true});
      }
    } else {
      this.setState({ voteError: true});
    }
    this.setState({ voting: false })
  };

  // Select a file to upload
  onFileSelected = async () => {
      // Access JavaScript FileReader() method for conversion to buffer
      const reader = new FileReader();
      const file = this.fileInput.files[0];
      this.setState({fileLoading: true});
      if (file instanceof Blob ) {
          let {files_array, fileNames_array, fileHashes_array} = this.state;
          files_array.push(file);
          fileNames_array.push(file.name);
          this.setState({
              files_array: files_array,
              fileNames_array: fileNames_array
          });
          console.log("fileNames_array: ", fileNames_array);
          reader.onloadend = async () => {
              await this.setState({
                  fileUrl: reader.result,
                  fileLoading: true,
                  buffer: Buffer.from(reader.result) // File is converted to a buffer for upload to IPFS
              });
              const fileHash = this.state.buffer ? (await getIpfsHash(file)) : '0';
              fileHashes_array.push(fileHash);
              this.setState({ fileHashes_array: fileHashes_array });
              console.log("fileHashes_array: ", fileHashes_array);
              this.setState({fileLoading: false});
          }
          reader.readAsDataURL(file);
      }
  }

  // Remove a previously selected file to upload
  onFileRemoved = (file) => {
      var i = 0;
      let {files_array, fileHashes_array, fileNames_array} = this.state;
      console.log('file: ', file);
      for (i = 0 ; i < files_array.length ; i++) {
          if (file === files_array[i]) {
              files_array.splice(i, 1);
              fileNames_array.splice(i, 1);
              fileHashes_array.splice(i, 1);
              break;
          }
      }
      this.setState({
          files_array: files_array,
          fileNames_array: fileNames_array,
          fileHashes_array: fileHashes_array
      });
      console.log('fileNames_array', fileNames_array);
      console.log("fileHashes_array: ", fileHashes_array);
  }

  renderQuestion() {
      const summary = this.props.summary;
      const time = this.props.time;
      var currentTime = (new Date).getTime();
      var maxDuration =(parseFloat(summary[3]) / 60 / 60).toFixed(2).toString().toString();
      var publishTime = moment.unix(time[0]).format('dddd, Do MMMM YYYY, h:mm:ss a');
      var votingTime = moment.unix(this.state.votingTime).format('dddd, Do MMMM YYYY, h:mm:ss a');
      var publishTimeMs = moment.unix(time[0]).valueOf();
      var maxDurationMs = moment.unix(time[2]).valueOf();
      var votingTimeMs = moment.unix(this.state.votingTime).valueOf();

      let remainingTime = 0;
      if (this.state.questionState == 0) {
        remainingTime = publishTimeMs + maxDurationMs - currentTime;
        if (remainingTime < 0) {
            remainingTime = 0;
        }
      } else {
        remainingTime = votingTimeMs + maxDurationMs - currentTime;
        if (remainingTime < 0) {
            remainingTime = 0;
        }
      }

      return (
        <React.Fragment>
            <Table definition>
                <Table.Body>
                  <Table.Row>
                      <Table.Cell width={2}>Question Title</Table.Cell>
                      <Table.Cell style={{fontSize: '20px', lineHeight: '1.5'}}>
                          {summary[0]}
                      </Table.Cell>
                  </Table.Row>
                    <Table.Row>
                        <Table.Cell width={2}>Description</Table.Cell>
                        <Table.Cell style={{fontSize: '20px', lineHeight: '1.5'}}>
                            <Context
                                input='tex'
                                onLoad={ () => console.log("Loaded MathJax script!") }
                                onError={ (MathJax, error) => {
                                    console.warn(error);
                                    console.log("Encountered a MathJax error, re-attempting a typeset!");
                                    MathJax.Hub.Queue(
                                      MathJax.Hub.Typeset()
                                    );
                                } }
                                options={{
                                    asciimath2jax: {
                                         useMathMLspacing: true,
                                         delimiters: [["$$","$$"]],
                                         preview: "none",
                                    }
                                }}>
                                <Text text={summary[1]}/>
                            </Context>
                        </Table.Cell>
                    </Table.Row>
                    <Table.Row>
                        <Table.Cell>Reward (EQT)</Table.Cell>
                        <Table.Cell style={{fontSize: '20px'}}>{summary[2]*1e-4}
                        {this.state.questionState == 0
                            ?  <Popup
                                trigger={
                                    <Button style={{float: 'right', verticalAlign: 'middle'}}
                                            icon='ethereum'
                                            color="black"
                                            onClick={this.increaseReward}
                                              />
                                }
                                content={"Increase Reward"}
                                position='bottom right'
                                inverted />
                            :  <Popup
                                trigger={
                                    <Button style={{float: 'right', verticalAlign: 'middle'}}
                                            icon='ethereum'
                                            color="black"
                                            disabled
                                    />
                                }
                                position='bottom right'
                                inverted />
                        }
                        </Table.Cell>
                    </Table.Row>
                    <Table.Row>
                        <Table.Cell>Publish Time</Table.Cell>
                        <Table.Cell style={{fontSize: '20px'}}>{publishTime}</Table.Cell>
                    </Table.Row>
                    <Table.Row>
                        <Table.Cell>Answering Duration (hours)</Table.Cell>
                        <Table.Cell style={{fontSize: '20px'}}>
                            <span style={{verticalAlign: 'middle', lineHeight: '33px'}}>
                                {maxDuration}
                            </span>
                            {this.state.questionState == 0 && remainingTime > 0
                                ? <Popup
                                    trigger={
                                        <Button style={{float: 'right', verticalAlign: 'middle'}}
                                                icon='clock'
                                                color="violet"
                                        />}
                                    content={
                                        <span>
                                        <span>Remaining time - </span>
                                        <Countdown date={Date.now() + remainingTime}>
                                        </Countdown>
                                    </span>}
                                    position='bottom right'
                                    inverted />
                                : this.state.questionState == 0 && remainingTime == 0
                                ? <Popup trigger={<Button style={{float: 'right', verticalAlign: 'middle'}}
                                                          icon='clock'
                                                          color="red"/>}
                                        flowing hoverable>
                                    <Grid centered divided columns={2}>
                                      <Grid.Column textAlign='center'>
                                        <p>Extends the validity of question by 1 hour</p>
                                        <Button onClick={this.timeExtension}>Extend Deadline</Button>
                                      </Grid.Column>
                                      <Grid.Column textAlign='center'>
                                        <p>Proceed to the voting phase</p>
                                        <Button onClick={this.votePhase}>Voting Phase</Button>
                                      </Grid.Column>
                                    </Grid>
                                  </Popup>
                                : <Popup
                                    trigger={
                                        <Button style={{float: 'right', verticalAlign: 'middle'}}
                                                icon='clock'
                                                color="red"
                                                disabled
                                        />
                                    }
                                    position='bottom right'
                                    inverted />
                            }
                        </Table.Cell>
                    </Table.Row>
                    <Table.Row>
                        <Table.Cell>Voting Duration (hours)</Table.Cell>
                        <Table.Cell style={{fontSize: '20px'}}>
                            <span style={{verticalAlign: 'middle', lineHeight: '33px'}}>
                                {maxDuration}
                            </span>
                            {this.state.questionState == 1 && remainingTime > 0
                                ? <Popup
                                    trigger={
                                        <Button style={{float: 'right', verticalAlign: 'middle'}}
                                                icon='clock'
                                                color="violet"
                                        />}
                                    content={
                                        <span>
                                        <span>Remaining time - </span>
                                        <Countdown date={Date.now() + remainingTime}>
                                        </Countdown>
                                    </span>}
                                    position='bottom right'
                                    inverted />
                                : this.state.questionState == 1 && remainingTime == 0
                                ? <Popup
                                    trigger={
                                        <Button style={{float: 'right', verticalAlign: 'middle'}}
                                                icon='clock'
                                                color="red"
                                                onClick={this.rewardPhase}
                                                  />
                                    }
                                    content={"Reward"}
                                    position='bottom right'
                                    inverted />
                                : this.state.questionState == 0
                                ? <Popup
                                    trigger={
                                        <Button style={{float: 'right', verticalAlign: 'middle'}}
                                                icon='clock'
                                                color="red"
                                                disabled
                                        />
                                    }
                                    position='bottom right'
                                    inverted />
                                : <Popup
                                    trigger={
                                        <Button style={{float: 'right', verticalAlign: 'middle'}}
                                                icon='clock'
                                                color="red"
                                                disabled
                                                  />
                                    }
                                    position='bottom right'
                                    inverted />
                          }
                        </Table.Cell>
                    </Table.Row>
                </Table.Body>
            </Table>
        </React.Fragment>
      );
  }

  renderAnswers() {
    const answers = this.props.answerList;
    // Answering Phase no answers are shown
    if (this.state.questionState != 0){
      return answers.map((answer, index) => {
          var answeredTime = moment.unix(answer[5]).format('Do MMMM YYYY, h:mm a');
          var id = index % 70;
          return <Comment key={index}>
              <Comment.Avatar src={this.props.avatarListIcon[id]} style={{backgroundColor: '#8D8741'}}/>
              <Comment.Content style={{width:"1000px"}}>
                <Comment.Author as='a'>{answer[1]}</Comment.Author>
                <Comment.Metadata>
                  <div>{answeredTime}</div>
                </Comment.Metadata>
                <br/>
                {this.state.questionState == 2
                  ? <Comment.Metadata>
                    <div><Icon name='thumbs up' />{answer[3]}</div>
                    <div><Icon name='thumbs down' />{answer[4]}</div>
                  </Comment.Metadata>
                  : <Comment.Metadata>
                    <div><Icon name='thumbs up' />0</div>
                    <div><Icon name='thumbs down' />0</div>
                  </Comment.Metadata>
                }
                <Comment.Text>
                  <Context
                      input='tex'
                      onLoad={ () => console.log("Loaded MathJax script!") }
                      onError={ (MathJax, error) => {
                          console.warn(error);
                          console.log("Encountered a MathJax error, re-attempting a typeset!");
                          MathJax.Hub.Queue(
                            MathJax.Hub.Typeset()
                          );
                      } }
                      options={{
                          asciimath2jax: {
                               useMathMLspacing: true,
                               delimiters: [["$$","$$"]],
                               preview: "none",
                          }
                      }}>
                      <Text text={answer[0]}/>
                  </Context>
                  {answer[8].map((imageName, index) =>
                      <div style={{marginBottom: '10px'}} key={index}>
                          <Image src={"https://ipfs.io/ipfs/"+answer[9][index]} />
                      </div>
                  )}
                </Comment.Text>
                <Comment.Actions>
                  <Comment.Action>
                    <Button style={{float: 'right', verticalAlign: 'middle'}}
                            icon='thumbs down'
                            onClick={() => this.downVote(index)}
                            size='tiny'
                    />
                    <Button style={{float: 'right', verticalAlign: 'middle'}}
                            icon='thumbs up'
                            onClick={() => this.upVote(index)}
                            size='tiny'
                    />
                      </Comment.Action>
                </Comment.Actions>
              </Comment.Content>
            </Comment>
      });
    }
  }

  renderFiles () {
    let fileHashes = this.props.summary[5];
    let fileNames = this.props.summary[6];

    if (fileHashes.length == 0) {
        return(
            <Segment placeholder>
                <Header icon>
                    <Icon name='images outline' />
                    No images are uploaded for this question
                </Header>
            </Segment>
        );
    } else {
        return(
            <Segment placeholder>
                <center>
                    <p>Image(s) Uploaded</p>
                    {fileNames.map((fileName, index) =>
                        <div style={{marginBottom: '10px'}} key={index}>
                            <Image src={"https://ipfs.io/ipfs/"+fileHashes[index]} />
                            <Divider hidden/>
                            <p> To download: </p>
                            <Label as='a' size='big' href={"https://ipfs.io/ipfs/"+fileHashes[index]}>
                                <Icon name='download' />
                                {fileName}
                            </Label>
                        </div>
                    )}
                </center>
            </Segment>
        );
    }
  }

  renderAnswersForm() {
    let {files_array} = this.state;
    let elmFiles = null;

    if (files_array !== null) {
        elmFiles = files_array.map((item, index) =>
            <Label as='a' key={index} size='big'>
                {item.name}
                <Icon name='delete'
                      onClick={() => this.onFileRemoved(item)} />
            </Label>
        );
    }
    return(
      <Form error={!!this.state.errorMessage}>
        <Form.TextArea
          placeholder="Enter Answers"
          value={this.state.answer}
          onChange={event => this.setState({ answer: event.target.value })}
        />
        <Message error header="Oops!" content={this.state.errorMessage} />
        <Form.Field>
          {this.renderFilesUpload(elmFiles)}
        </Form.Field>
        <Modal open={this.state.loading} trigger={ this.state.questionState == 1 ? <Button content='Submit Answers' disabled labelPosition='left' icon='edit' primary />
            : <Button content='Submit Answers' onClick={this.onSubmitAnswer} loading={this.state.loading} labelPosition='left' icon='edit' primary />} basic size='small'>
          <Header content='Posting New Answers' />
          <Modal.Content>
            <p>
              Please Confirm the MetaMask Transaction Request to submit your Answers to the Block-Chain Network.
              Upon successful submission, you will be redirected back to the Question Page. This process might take awhile.
            </p>
            <Loader active inline="centered">
              Loading
            </Loader>
          </Modal.Content>
        </Modal>
      </Form>
    );
  }

  renderFilesUpload(files) {
    let {files_array} = this.state;
    if(this.state.files_array.length == 0){
      return (
        <Segment placeholder>
          <Header icon>
            <Icon name='images outline' />
            No images are uploaded for this answer
          </Header>
          <input
              style={{ display: 'none' }}
              type='file'
              onChange={() => this.onFileSelected()}
              ref={fileInput => this.fileInput = fileInput}/>
          <Button primary onClick={() => this.fileInput.click()} loading={this.state.fileLoading}>Upload Image</Button>
        </Segment>
      )
    } else {
      return (
        <Segment placeholder>
          <center>
              <div style={{marginBottom: '20px'}}>
                  {files}
              </div>
              <input
                  style={{ display: 'none' }}
                  type='file'
                  onChange={() => this.onFileSelected()}
                  ref={fileInput => this.fileInput = fileInput}/>
              <Button primary onClick={() => this.fileInput.click()} loading={this.state.fileLoading}>Upload Files</Button>
          </center>
        </Segment>
      )
    }
  };

  render() {
    if (this.state.login) {
      return (
        <Layout accountType={this.state.accountType}>
          <Container>
            <Divider hidden/>
            {this.renderQuestion()}
            <Divider />
            {this.renderFiles()}
            <Header as='h2' textAlign='center'>
                Submit Your Answers Here
            </Header>
            <p><b>Note:</b> To include math equations, delimit the latex format with $$.</p>
            <a style={{display: "table-cell"}} href="https://www.codecogs.com/latex/eqneditor.php" target="_blank">Link to Supported Latex Editor</a>
            {this.renderAnswersForm()}
            <Header as='h2' textAlign='center'>
              Answer(s) Submitted: {this.props.answerList.length}
            </Header>
            <Divider />
            <Comment.Group size='large'>
            {this.renderAnswers()}
            </Comment.Group>
            <Modal open={this.state.voteError} basic size='small'>
              <Header icon='ethereum' content='Cannot Vote Own Answers' />
              <Modal.Content>
                <p>
                  You have selected to vote the answer you have provided. Please only vote answers provided by
                  others.
                </p>
              </Modal.Content>
              <Modal.Actions>
                <Button color='green' inverted attached='bottom' onClick={() => {
                  Router.pushRoute(`/questions/${this.state.questionAddress}`)
                  this.setState({ voteError: false})}
                }>
                  Go Back
                </Button>
              </Modal.Actions>
            </Modal>
            <Modal open={this.state.ownerError} basic size='small'>
              <Header icon='ethereum' content='Only Available For Owner Of Question' />
              <Modal.Content>
                <p>
                  You cannot change the state of the question as you are not the owner.
                </p>
              </Modal.Content>
              <Modal.Actions>
                <Button color='green' inverted attached='bottom' onClick={() => {
                  Router.pushRoute(`/questions/${this.state.questionAddress}`)
                  this.setState({ ownerError: false})}
                }>
                  Go Back
                </Button>
              </Modal.Actions>
            </Modal>
            <Modal open={this.state.voteMultipleError} basic size='small'>
              <Header icon='ethereum' content='Answer Already Voted' />
              <Modal.Content>
                <p>
                  You cannot vote for the same answer more than once. Please vote for another answer.
                </p>
              </Modal.Content>
              <Modal.Actions>
                <Button color='green' inverted attached='bottom' onClick={() => {
                  Router.pushRoute(`/questions/${this.state.questionAddress}`)
                  this.setState({ voteMultipleError: false})}
                }>
                  Go Back
                </Button>
              </Modal.Actions>
            </Modal>
            <Modal open={this.state.stateChange} basic size='small'>
              <Header content='Proceeding To Voting/Rewarding Phase' />
              <Modal.Content>
                <p>
                  Please Confirm the MetaMask Transaction Request to proceed towards Voting/Rewarding Phase.
                </p>
                <Loader active inline="centered">
                  State Changing
                </Loader>
              </Modal.Content>
            </Modal>
            <Modal open={this.state.timeExtension} basic size='small'>
              <Header content='Extending Question Deadline' />
              <Modal.Content>
                <p>
                  Please Confirm the MetaMask Transaction Request to extend the duration of the question.
                </p>
                <Loader active inline="centered">
                  Refreshing Question
                </Loader>
              </Modal.Content>
            </Modal>
            <Modal open={this.state.changeReward} basic size='small'>
              <Header content='Increasing Reward' />
              <Modal.Content>
                <p>
                  Please Confirm the MetaMask Transaction Request to increase reward.
                </p>
                <Loader active inline="centered">
                  Increasing Reward
                </Loader>
              </Modal.Content>
            </Modal>
            <Modal open={this.state.timeExtension} basic size='small'>
              <Header content='Extending Question Deadline' />
              <Modal.Content>
                <p>
                  Please Confirm the MetaMask Transaction Request to extend the duration of the question.
                </p>
                <Loader active inline="centered">
                  Refreshing Question
                </Loader>
              </Modal.Content>
            </Modal>
            <Modal open={this.state.voting} basic size='small'>
              <Header content='Voting Answers' />
              <Modal.Content>
                <p>
                  Please Confirm the MetaMask Transaction Request to up/down vote. <b>You cannot change your vote
                  upon submission</b>.
                </p>
                <Loader active inline="centered">
                  Voting
                </Loader>
              </Modal.Content>
            </Modal>
          </Container>
        </Layout>
      );
    } else {
      return (
        <Layout accountType={this.state.accountType}>
          <Container style={{width: "100%", height:"100vh"}}>
            <Modal open={this.state.resourceLoading} basic size='small'>
              <Header icon='ethereum' content='Loading Resources' />
              <Modal.Content>
                <Loader active inline="centered">
                  Loading Selected Question Information
                </Loader>
              </Modal.Content>
            </Modal>
            <Modal open={!this.state.login && !this.state.resourceLoading} basic size='small'>
              <Header icon='ethereum' content='Not Registered' />
              <Modal.Content>
                <p>
                  You are currently not registered to access the platform. Please have your account registered.
                </p>
              </Modal.Content>
              <Modal.Actions>
                <Button color='green' inverted attached='bottom' onClick={() => {
                  Router.pushRoute('/')}
                }>
                  Register For Account
                </Button>
              </Modal.Actions>
            </Modal>
          </Container>
        </Layout>
      );
    }
  }
}

export default QuestionRow;
