import React, { Component } from 'react';
import { Item } from 'semantic-ui-react';
import { Link } from '../routes';

class QuestionRow extends Component {
    render() {
        const { summary } = this.props;

        return (
          <Item>
            <Item.Content>
              <Item.Header as='a'>{summary[0]}</Item.Header>
              <Item.Meta>Reward:{summary[2]}</Item.Meta>
              <Item.Description>
                {summary[1]}
              </Item.Description>
              <Item.Extra>Duration:{summary[3]}</Item.Extra>
            </Item.Content>
          </Item>
        );
    }
}

export default PostRow;
